import React, { useState } from "react";
import { FaUser, FaClock, FaPlug, FaSave, FaArrowLeft, FaPlusCircle, FaHeadset } from "react-icons/fa";

function AccountSettings({
  setShowAccountSettings,
  rooms = [],
  userData = {
    account: {
      firstName: "",
      lastName: "",
      emails: [""],
      phoneNumbers: [""],
      address: {
        line1: "",
        line2: "",
        city: "",
        state: "",
        zipCode: ""
      }
    },
    devices: []
  }
}) {
  const [activeTab, setActiveTab] = useState("profile");
  const [selectedTimes, setSelectedTimes] = useState({});
  const [isSelecting, setIsSelecting] = useState(false);
  const [selectionStart, setSelectionStart] = useState(null);
  const [currentSelection, setCurrentSelection] = useState(null);
  const [selectionValue, setSelectionValue] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [alwaysOnDevices, setAlwaysOnDevices] = useState([]);
  const [searchType, setSearchType] = useState("name");
  const [formData, setFormData] = useState({
    firstName: userData.account.firstName,
    lastName: userData.account.lastName,
    email: userData.account.emails[0],
    phone: userData.account.phoneNumbers[0],
    address: {
      line1: userData.account.address.line1,
      line2: userData.account.address.line2,
      city: userData.account.address.city,
      state: userData.account.address.state,
      zipCode: userData.account.address.zipCode
    }
  });

  // Customer support state
  const [supportMessage, setSupportMessage] = useState("");
  const [supportCategory, setSupportCategory] = useState("general");

  // New state for add device tab
  const [newDevice, setNewDevice] = useState({
    type: "iot",
    kasaPlugId: "",
    deviceType: "",
    brand: "",
    customBrand: "",
    productType: "",
    model: "",
    serialNumber: "",
    room: rooms.length > 0 ? rooms[0].name : "Living Room"
  });

  const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
  const hours = Array.from({ length: 24 }, (_, i) => `${i}:00`);

  // Common IoT brands for dropdown
  const commonIoTBrands = [
    "Samsung",
    "LG",
    "GE",
    "Philips Hue",
    "Amazon",
    "Google",
    "Apple",
    "Nest",
    "Ecobee",
    "Ring",
    "Arlo",
    "TP-Link",
    "Wyze",
    "Honeywell",
    "Custom"
  ];

  // Device types for dropdown
  const deviceTypes = [
    "Light",
    "Thermostat",
    "Lock",
    "Camera",
    "Speaker",
    "TV",
    "Refrigerator",
    "Washer",
    "Dryer",
    "Dishwasher",
    "Oven",
    "Microwave",
    "Air Conditioner",
    "Fan",
    "Vacuum",
    "Other"
  ];

  // Support categories
  const supportCategories = [
    "General Inquiry",
    "Device Issues",
    "Connectivity Problems",
    "Account Management",
    "Billing Questions",
    "Feature Requests",
    "Other"
  ];

  // Handle support form submission
  const handleSupportSubmit = (e) => {
    e.preventDefault();
    // Here you would typically send the support request to an API
    console.log("Support request:", {
      category: supportCategory,
      message: supportMessage
    });

    // Show success message and reset form
    alert("Your support request has been submitted. We'll get back to you soon!");
    setSupportMessage("");
    setSupportCategory("general");
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name.includes(".")) {
      const [parent, child] = name.split(".");
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  // Handler for new device form
  const handleNewDeviceChange = (e) => {
    const { name, value } = e.target;
    setNewDevice(prev => ({
      ...prev,
      [name]: value
    }));

    // Special handling for brand selection
    if (name === "brand" && value === "Custom") {
      setNewDevice(prev => ({
        ...prev,
        brand: "Custom",
        customBrand: ""
      }));
    }
  };

  // Handler for adding a new device
  const handleAddDevice = (e) => {
    e.preventDefault();

    // Create the final device object
    const deviceToAdd = {
      ...newDevice,
      // If brand is Custom, use the customBrand value
      brand: newDevice.brand === "Custom" ? newDevice.customBrand : newDevice.brand
    };

    // Here you would typically make an API call to add the device
    console.log("Adding new device:", deviceToAdd);

    // Reset form
    setNewDevice({
      type: "iot",
      kasaPlugId: "",
      deviceType: "",
      brand: "",
      customBrand: "",
      productType: "",
      model: "",
      serialNumber: "",
      room: rooms.length > 0 ? rooms[0].name : "Living Room"
    });

    // Show success message
    alert("Device added successfully!");
  };

  const filteredDevices = userData.devices.filter(device => {
    const isNotAlreadySelected = !alwaysOnDevices.some(d => d.id === device.id);
    if (!searchTerm) return isNotAlreadySelected;
    if (searchType === "id") {
      return device.id.toString() === searchTerm && isNotAlreadySelected;
    }
    return device.name.toLowerCase().includes(searchTerm.toLowerCase()) && isNotAlreadySelected;
  });

  const addDevice = (device) => {
    setAlwaysOnDevices(prev => [...prev, device]);
    setSearchTerm("");
  };

  const removeDevice = (deviceId) => {
    setAlwaysOnDevices(prev => prev.filter(d => d.id !== deviceId));
  };

  const getCellCoordinates = (day, hour) => {
    const dayIndex = daysOfWeek.indexOf(day);
    const hourIndex = hours.indexOf(hour);
    return { dayIndex, hourIndex };
  };

  const isCellInRange = (day, hour, start, end) => {
    if (!start || !end) return false;

    const cell = getCellCoordinates(day, hour);
    const startCoords = getCellCoordinates(start.day, start.hour);
    const endCoords = getCellCoordinates(end.day, end.hour);

    const minDayIndex = Math.min(startCoords.dayIndex, endCoords.dayIndex);
    const maxDayIndex = Math.max(startCoords.dayIndex, endCoords.dayIndex);
    const minHourIndex = Math.min(startCoords.hourIndex, endCoords.hourIndex);
    const maxHourIndex = Math.max(startCoords.hourIndex, endCoords.hourIndex);

    return (
      cell.dayIndex >= minDayIndex &&
      cell.dayIndex <= maxDayIndex &&
      cell.hourIndex >= minHourIndex &&
      cell.hourIndex <= maxHourIndex
    );
  };

  const handleMouseDown = (day, hour) => {
    setIsSelecting(true);
    setSelectionStart({ day, hour });
    setCurrentSelection({ day, hour });
    const currentValue = selectedTimes[day]?.[hour] || false;
    setSelectionValue(!currentValue);
    setSelectedTimes(prev => {
      const updated = { ...prev };
      if (!updated[day]) updated[day] = {};
      updated[day][hour] = !currentValue;
      return updated;
    });
  };

  const handleMouseEnter = (day, hour) => {
    if (isSelecting) {
      setCurrentSelection({ day, hour });
    }
  };

  const handleMouseUp = () => {
    if (isSelecting && selectionStart && currentSelection) {
      const updatedTimes = { ...selectedTimes };
      daysOfWeek.forEach(day => {
        hours.forEach(hour => {
          if (isCellInRange(day, hour, selectionStart, currentSelection)) {
            if (!updatedTimes[day]) updatedTimes[day] = {};
            updatedTimes[day][hour] = selectionValue;
          }
        });
      });
      setSelectedTimes(updatedTimes);
    }
    setIsSelecting(false);
    setSelectionStart(null);
    setCurrentSelection(null);
  };

  const handleSave = () => {
    // Here you would typically make an API call to save the changes
    console.log("Saving changes", {
      profile: formData,
      schedule: selectedTimes,
      alwaysOnDevices
    });
  };

  // Profile Tab
  const renderProfileTab = () => (
    <div>
      <h2 className="text-lg font-semibold mb-4">User Profile</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label htmlFor="firstName" className="block text-sm font-medium text-gray-700 mb-1">
              First Name
            </label>
            <input
              type="text"
              id="firstName"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
              className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              required
            />
          </div>

          <div>
            <label htmlFor="lastName" className="block text-sm font-medium text-gray-700 mb-1">
              Last Name
            </label>
            <input
              type="text"
              id="lastName"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
              className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              required
            />
          </div>
        </div>

        <div>
          <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
            Email Address
          </label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            required
          />
        </div>

        <div>
          <label htmlFor="phone" className="block text-sm font-medium text-gray-700 mb-1">
            Phone Number
          </label>
          <input
            type="tel"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleInputChange}
            className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          />
        </div>

        <div className="pt-4">
          <label htmlFor="address" className="block text-lg font-medium text-gray-800 mb-2">
            Home Address
          </label>
          <div className="space-y-4">
            <div>
              <label htmlFor="street" className="block text-sm font-medium text-gray-700 mb-1">
                Street Address
              </label>
              <input
                type="text"
                id="street"
                name="street"
                value={formData.street}
                onChange={handleInputChange}
                className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label htmlFor="city" className="block text-sm font-medium text-gray-700 mb-1">
                  City
                </label>
                <input
                  type="text"
                  id="city"
                  name="city"
                  value={formData.city}
                  onChange={handleInputChange}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>

              <div>
                <label htmlFor="state" className="block text-sm font-medium text-gray-700 mb-1">
                  State
                </label>
                <input
                  type="text"
                  id="state"
                  name="state"
                  value={formData.state}
                  onChange={handleInputChange}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>

              <div>
                <label htmlFor="zipCode" className="block text-sm font-medium text-gray-700 mb-1">
                  ZIP Code
                </label>
                <input
                  type="text"
                  id="zipCode"
                  name="zipCode"
                  value={formData.zipCode}
                  onChange={handleInputChange}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
            </div>
          </div>
        </div>
      </form>
    </div>
  );

  const renderScheduleTab = () => (
    <div className="space-y-4">
      <p className="text-sm text-gray-600">
        Click and drag to select hours when you are typically away from home.
        This helps optimize your energy usage during these periods.
      </p>
      <div
        className="bg-white rounded-lg shadow-sm border"
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
      >
        <div className="grid grid-cols-8 gap-px bg-gray-200">
          <div className="bg-white p-2 font-medium text-center">Time</div>
          {daysOfWeek.map((day, index) => (
            <div key={index} className="bg-white p-2 font-medium text-center">
              {day.slice(0, 3)}
            </div>
          ))}
        </div>

        {hours.map((hour, rowIndex) => (
          <div key={rowIndex} className="grid grid-cols-8 gap-px bg-gray-200">
            <div className="bg-white p-2 text-center text-sm">
              {hour}
            </div>
            {daysOfWeek.map((day, colIndex) => {
              const isSelected = selectedTimes[day]?.[hour] || false;
              const isInSelectionRange = isSelecting &&
                isCellInRange(day, hour, selectionStart, currentSelection);
              return (
                <div
                  key={`${day}-${hour}`}
                  className={`
                    bg-white cursor-pointer select-none transition-colors
                    ${isSelected ? "bg-blue-500" : "hover:bg-gray-100"}
                    ${isInSelectionRange ? "bg-blue-200" : ""}
                  `}
                  onMouseDown={() => handleMouseDown(day, hour)}
                  onMouseEnter={() => handleMouseEnter(day, hour)}
                />
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );

  const renderDevicesTab = () => (
    <div className="space-y-6">
      <div className="flex gap-4">
        <select
          value={searchType}
          onChange={(e) => setSearchType(e.target.value)}
          className="p-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
        >
          <option value="name">Search by Name</option>
          <option value="id">Search by ID</option>
        </select>
        <input
          type="text"
          placeholder={searchType === "id" ? "Enter device ID..." : "Search devices..."}
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="flex-1 p-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
        />
      </div>

      <div className="grid md:grid-cols-2 gap-6">
        <div className="space-y-4">
          <h3 className="font-medium">Available Devices</h3>
          <div className="border rounded-lg divide-y max-h-96 overflow-y-auto">
            {filteredDevices.map(device => (
              <div
                key={device.id}
                className="p-3 flex items-center justify-between hover:bg-gray-50"
              >
                <div>
                  <p className="font-medium">{device.name}</p>
                  <p className="text-sm text-gray-600">
                    ID: {device.id} • {device.room}
                  </p>
                </div>
                <button
                  onClick={() => addDevice(device)}
                  className="px-3 py-1 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                >
                  Add
                </button>
              </div>
            ))}
            {searchTerm && filteredDevices.length === 0 && (
              <p className="p-4 text-center text-gray-500">No devices found</p>
            )}
          </div>
        </div>

        <div className="space-y-4">
          <h3 className="font-medium">Always-On Devices</h3>
          <div className="border rounded-lg divide-y max-h-96 overflow-y-auto">
            {alwaysOnDevices.map(device => (
              <div
                key={device.id}
                className="p-3 flex items-center justify-between hover:bg-gray-50"
              >
                <div>
                  <p className="font-medium">{device.name}</p>
                  <p className="text-sm text-gray-600">
                    {device.room} • {device.watts}W
                  </p>
                </div>
                <button
                  onClick={() => removeDevice(device.id)}
                  className="px-3 py-1 bg-red-500 text-white rounded-lg hover:bg-red-600"
                >
                  Remove
                </button>
              </div>
            ))}
            {alwaysOnDevices.length === 0 && (
              <p className="p-4 text-center text-gray-500">No devices selected</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );

  // New Add Device Tab
  const renderAddDeviceTab = () => (
    <div className="space-y-6">
      <h2 className="text-lg font-semibold mb-4">Add New Device</h2>

      <form onSubmit={handleAddDevice} className="space-y-4">
        {/* Device Type Selection */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">Device Category</label>
          <div className="grid grid-cols-2 gap-3">
            <div 
              className={`border rounded-lg p-4 text-center cursor-pointer transition-colors ${newDevice.type === "iot" ? "bg-blue-50 border-blue-500" : "hover:bg-gray-50"}`}
              onClick={() => setNewDevice(prev => ({ ...prev, type: "iot" }))}
            >
              <div className="flex justify-center mb-2">
                <svg className="w-8 h-8 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 3v2m6-2v2M9 19v2m6-2v2M5 9H3m2 6H3m18-6h-2m2 6h-2M7 19h10a2 2 0 002-2V7a2 2 0 00-2-2H7a2 2 0 00-2 2v10a2 2 0 002 2zM9 9h6v6H9V9z" />
                </svg>
              </div>
              <div className="font-medium">IoT Device</div>
              <div className="text-xs text-gray-500 mt-1">Smart devices with direct network connectivity</div>
            </div>

            <div 
              className={`border rounded-lg p-4 text-center cursor-pointer transition-colors ${newDevice.type === "non-iot" ? "bg-blue-50 border-blue-500" : "hover:bg-gray-50"}`}
              onClick={() => setNewDevice(prev => ({ ...prev, type: "non-iot" }))}
            >
              <div className="flex justify-center mb-2">
                <svg className="w-8 h-8 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
                </svg>
              </div>
              <div className="font-medium">Non-IoT Device</div>
              <div className="text-xs text-gray-500 mt-1">Traditional devices connected via smart plug</div>
            </div>
          </div>
        </div>

        {/* Smart Plug ID - for Non-IoT only */}
        {newDevice.type === "non-iot" && (
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-1">Kasa Smart Plug ID</label>
            <input
              type="text"
              name="kasaPlugId"
              value={newDevice.kasaPlugId}
              onChange={handleNewDeviceChange}
              className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500"
              placeholder="Enter smart plug ID"
              required
            />
            <p className="text-xs text-gray-500 mt-1">Find this on your Kasa Smart Plug device or app</p>
          </div>
        )}

        {/* Common Fields for Both Types */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-1">Device Type</label>
          <select
            name="deviceType"
            value={newDevice.deviceType}
            onChange={handleNewDeviceChange}
            className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500"
            required
          >
            <option value="" disabled>Select device type</option>
            {deviceTypes.map(type => (
              <option key={type} value={type}>{type}</option>
            ))}
          </select>
        </div>

        {/* Brand Field */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-1">
            {newDevice.type === "iot" ? "Brand" : "Brand (Optional)"}
          </label>
          {newDevice.type === "iot" ? (
            <div className="space-y-3">
              <select
                name="brand"
                value={newDevice.brand}
                onChange={handleNewDeviceChange}
                className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500"
                required
              >
                <option value="" disabled>Select brand</option>
                {commonIoTBrands.map(brand => (
                  <option key={brand} value={brand}>{brand}</option>
                ))}
              </select>

              {/* Custom brand input field */}
              {newDevice.brand === "Custom" && (
                <input
                  type="text"
                  name="customBrand"
                  value={newDevice.customBrand}
                  onChange={handleNewDeviceChange}
                  className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500"
                  placeholder="Enter brand name"
                  required
                />
              )}
            </div>
          ) : (
            <input
              type="text"
              name="brand"
              value={newDevice.brand}
              onChange={handleNewDeviceChange}
              className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500"
              placeholder="Enter brand (optional)"
            />
          )}
        </div>

        {/* Additional IoT Fields */}
        {newDevice.type === "iot" && (
          <>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Product Type</label>
              <input
                type="text"
                name="productType"
                value={newDevice.productType}
                onChange={handleNewDeviceChange}
                className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500"
                placeholder="e.g., Smart Bulb, Smart Thermostat"
                required
              />
            </div>

            <div className="grid grid-cols-2 gap-4 mb-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Model</label>
                <input
                  type="text"
                  name="model"
                  value={newDevice.model}
                  onChange={handleNewDeviceChange}
                  className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500"
                  placeholder="Model number"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Serial Number</label>
                <input
                  type="text"
                  name="serialNumber"
                  value={newDevice.serialNumber}
                  onChange={handleNewDeviceChange}
                  className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500"
                  placeholder="Serial number"
                  required
                />
              </div>
            </div>
          </>
        )}

        {/* Room Selection */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-1">Room</label>
          <select
            name="room"
            value={newDevice.room}
            onChange={handleNewDeviceChange}
            className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500"
            required
          >
            {rooms.length > 0 ? (
              rooms.map(room => (
                <option key={room.name} value={room.name}>{room.name}</option>
              ))
            ) : (
              <option value="Living Room">Living Room</option>
            )}
          </select>
        </div>

        {/* Submit Button */}
        <div className="pt-4">
          <button
            type="submit"
            className="w-full p-3 bg-blue-500 text-white rounded-lg font-medium hover:bg-blue-600 transition-colors"
          >
            Add Device
          </button>
        </div>
      </form>
    </div>
