import React, { useState } from 'react';
import { ThermometerIcon, ChevronUp, ChevronDown } from 'lucide-react';

const ThermostatControl = ({ temperature = 72, unit = '°F', minTemp = 50, maxTemp = 90 }) => {
  const [currentTemp, setCurrentTemp] = useState(temperature);

  const increaseTemp = () => {
    if (currentTemp < maxTemp) {
      setCurrentTemp(prev => prev + 1);
    }
  };

  const decreaseTemp = () => {
    if (currentTemp > minTemp) {
      setCurrentTemp(prev => prev - 1);
    }
  };

  return (
    <div className="bg-white p-4 rounded-xl shadow-lg border border-gray-200 h-full flex flex-col">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-semibold text-gray-800">Thermostat</h2>
        <ThermometerIcon className="w-6 h-6 text-blue-500" />
      </div>

      <div className="flex-1 flex items-center justify-center">
        <div className="flex items-center gap-4">
          <button 
            onClick={decreaseTemp}
            className="p-3 rounded-full bg-gray-100 hover:bg-gray-200 active:bg-gray-300 transition-colors"
            aria-label="Decrease temperature"
          >
            <ChevronDown className="w-6 h-6" />
          </button>

          <div className="text-center">
            <div className="text-5xl font-bold text-blue-500">{currentTemp}</div>
            <div className="text-xl text-gray-500">{unit}</div>
          </div>

          <button 
            onClick={increaseTemp}
            className="p-3 rounded-full bg-gray-100 hover:bg-gray-200 active:bg-gray-300 transition-colors"
            aria-label="Increase temperature"
          >
            <ChevronUp className="w-6 h-6" />
          </button>
        </div>
      </div>

      <div className="flex justify-between text-sm text-gray-600 mt-4">
        <span>Min: {minTemp}{unit}</span>
        <span>Max: {maxTemp}{unit}</span>
      </div>
    </div>
  );
};

export default ThermostatControl;
