import React, { useState, useEffect } from 'react';
import { Power, Activity, DollarSign, AlertCircle } from 'lucide-react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Tooltip } from '@/components/ui/tooltip';

const DeviceCard = ({ device, onToggle }) => {
  const [isOn, setIsOn] = useState(device.status === "On");
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    setIsOn(device.status === "On");
  }, [device.status]);

  const handlePowerClick = async (e) => {
    e.stopPropagation();
    setIsLoading(true);

    try {
      await onToggle(device);
      setIsOn(!isOn);
    } catch (error) {
      console.error('Failed to toggle device:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const getCostPerHour = (watts) => {
    const cost = (watts * 0.12) / 1000;
    return cost.toFixed(2);
  };

  const getStatusColor = () => {
    if (isLoading) return 'bg-yellow-100';
    return isOn ? 'bg-green-100' : 'bg-red-100';
  };

  return (
    <Card className={`transform transition-all duration-300 hover:scale-102 ${getStatusColor()}`}>
      <CardHeader className="p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <div className={`w-12 h-12 rounded-full flex items-center justify-center bg-white shadow-sm
              ${isOn ? 'ring-2 ring-green-400' : 'ring-1 ring-gray-200'}`}>
              {device.icon || <Activity className="w-6 h-6 text-gray-600" />}
            </div>
            <div>
              <h3 className="text-lg font-semibold flex items-center gap-2">
                {device.name}
                {device.status === "Error" && (
                  <Tooltip content="Device is experiencing issues">
                    <AlertCircle className="w-4 h-4 text-red-500" />
                  </Tooltip>
                )}
              </h3>
              <div className="flex items-center gap-2">
                <Badge variant={isOn ? "success" : "secondary"}>
                  {device.room}
                </Badge>
                <span className="text-sm text-gray-500">•</span>
                <span className="text-sm text-gray-600">
                  {isOn ? 'Active' : 'Standby'}
                </span>
              </div>
            </div>
          </div>

          <button
            onClick={handlePowerClick}
            disabled={isLoading}
            className={`p-3 rounded-full transition-all duration-300
              ${isOn 
                ? 'bg-green-100 hover:bg-green-200 text-green-600' 
                : 'bg-red-100 hover:bg-red-200 text-red-600'}
              ${isLoading ? 'animate-pulse cursor-wait' : 'cursor-pointer'}
              focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500`}
          >
            <Power className={`w-6 h-6 ${isLoading ? 'animate-spin' : ''}`} />
          </button>
        </div>
      </CardHeader>

      <CardContent className="p-4 pt-0">
        <div className="grid grid-cols-2 gap-4 mt-4">
          <div className="bg-white p-3 rounded-lg shadow-sm">
            <div className="flex items-center gap-2 text-gray-600 mb-1">
              <Activity className="w-4 h-4" />
              <span className="text-sm">Power Usage</span>
            </div>
            <p className="font-semibold text-lg">
              {device.watts || 0}
              <span className="text-sm text-gray-500 ml-1">W</span>
            </p>
          </div>

          <div className="bg-white p-3 rounded-lg shadow-sm">
            <div className="flex items-center gap-2 text-gray-600 mb-1">
              <DollarSign className="w-4 h-4" />
              <span className="text-sm">Cost/hr</span>
            </div>
            <p className="font-semibold text-lg">
              ${getCostPerHour(device.watts || 0)}
            </p>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

export default DeviceCard;
