import React, { useState } from "react";
import { Search, Power, TrendingDown, ChevronDown, Home } from 'lucide-react';
import WeatherWidget from "./WeatherWidget";
import DeviceCard from "./DeviceCard";
import LightingControl from "./LightingControl";
import ThermostatControl from "./ThermostatControl";
import { RoomEnergyCard, HomeEnergyPopup } from "./EnergyAnalytics";

const Dashboard = ({ user, selectedRoom, setSelectedDevice }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [showEnergyPopup, setShowEnergyPopup] = useState(false);
  const [activeTab, setActiveTab] = useState("all");

  const getFilteredDevices = () => {
    let devices = user.devices;

    // Apply search filter
    devices = devices.filter(device => 
      device.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Apply room filter
    if (selectedRoom) {
      devices = devices.filter(device => device.room === selectedRoom.name);
    }

    // Apply active/inactive filter
    if (activeTab === "active") {
      devices = devices.filter(device => device.status === "On");
    } else if (activeTab === "inactive") {
      devices = devices.filter(device => device.status !== "On");
    }

    return devices;
  };

  const filteredDevices = getFilteredDevices();

  return (
    <div className="w-full max-w-7xl mx-auto p-4 space-y-4">
      {/* Top Bar with Room Name or Welcome */}
      <div className="flex items-center justify-between gap-4 mb-4">
        <div className="flex items-center gap-4">
          {selectedRoom && (
            <button
              onClick={() => window.location.reload()}
              className="p-2 hover:bg-gray-100 rounded-full transition-colors"
            >
              <Home className="w-6 h-6" />
            </button>
          )}
          <h1 className="text-2xl font-bold text-gray-800">
            {selectedRoom ? selectedRoom.name : `Welcome, ${user.name}`}
          </h1>
        </div>

        {/* Search and Tabs Section */}
        <div className="relative w-1/3">
          <input
            type="text"
            placeholder="Search devices..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
        </div>
      </div>

      {/* Filter Tabs */}
      {!selectedRoom && (
        <div className="flex space-x-1 bg-gray-100 p-2 rounded-lg">
          <button
            onClick={() => setActiveTab("all")}
            className={`px-4 py-2 rounded-full text-sm font-medium transition-colors ${
              activeTab === "all" 
                ? "bg-blue-500 text-white" 
                : "bg-white text-gray-800 hover:bg-gray-200"
            }`}
          >
            All Devices
          </button>
          <button
            onClick={() => setActiveTab("active")}
            className={`px-4 py-2 rounded-full text-sm font-medium transition-colors ${
              activeTab === "active" 
                ? "bg-blue-500 text-white" 
                : "bg-white text-gray-800 hover:bg-gray-200"
            }`}
          >
            Active Devices
          </button>
          <button
            onClick={() => setActiveTab("inactive")}
            className={`px-4 py-2 rounded-full text-sm font-medium transition-colors ${
              activeTab === "inactive" 
                ? "bg-blue-500 text-white" 
                : "bg-white text-gray-800 hover:bg-gray-200"
            }`}
          >
            Inactive Devices
          </button>
        </div>
      )}

      {/* Room Energy Card (when room is selected) */}
      {selectedRoom && (
        <RoomEnergyCard room={selectedRoom} />
      )}

      {/* Main Dashboard Section - Only shown when no room is selected */}
      {!selectedRoom && (
        <>
          {/* Cost Summary Cards in smaller layout */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
            {/* Total Consumption Card - Clickable */}
            <div 
              className="bg-white p-4 rounded-xl shadow-lg border border-gray-200 cursor-pointer hover:shadow-xl transition-shadow"
              onClick={() => setShowEnergyPopup(true)}
            >
              <div className="flex items-center gap-3 mb-2">
                <Power className="w-6 h-6 text-blue-500" />
                <h2 className="text-xl font-semibold">Total Consumption</h2>
              </div>
              <p className="text-3xl font-bold text-blue-500">
                {user.totalConsumption} kWh
              </p>
              <div className="mt-2 flex items-center text-sm text-gray-600">
                <TrendingDown className="w-4 h-4 mr-1" />
                <span>Tap for detailed analytics</span>
              </div>
            </div>

            {/* Cost Summary */}
            <div className="bg-white p-4 rounded-xl shadow-lg border border-gray-200">
              <h2 className="text-xl font-semibold mb-2">Cost Summary</h2>
              <p className="text-3xl font-bold text-green-500">
                ${(user.totalConsumption * 0.12).toFixed(2)}
              </p>
              <div className="mt-2 flex items-center text-sm text-gray-600">
                <span className="flex items-center">
                  <svg className="w-4 h-4 mr-1" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M3 3a1 1 0 011-1h12a1 1 0 011 1v3a1 1 0 01-.293.707L12 11.414V15a1 1 0 01-.293.707l-2 2A1 1 0 018 17v-5.586L3.293 6.707A1 1 0 013 6V3z" clipRule="evenodd" />
                  </svg>
                  Based on $0.12 per kWh
                </span>
              </div>
            </div>
          </div>

          {/* Controls and Weather Section */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {/* Lighting Control - Left Side */}
            <div className="bg-white p-4 rounded-xl shadow-lg border border-gray-200">
              <div className="flex items-center justify-between mb-3">
                <div>
                  <h2 className="text-xl font-semibold text-gray-800">Lighting Control</h2>
                  <div className="flex items-center mt-1">
                    <button className="flex items-center text-sm text-blue-600">
                      Living Room <ChevronDown size={16} className="ml-1" />
                    </button>
                  </div>
                </div>
                <div className="p-2 rounded-full bg-yellow-100 flex items-center justify-center">
                  <div className="text-xl font-semibold">
                    <input 
                      type="number" 
                      min="0" 
                      max="100" 
                      className="w-12 bg-transparent text-center focus:outline-none" 
                      value="66"
                    />
                    <span className="text-gray-500">%</span>
                  </div>
                </div>
              </div>

              <div className="space-y-3">
                {/* Slider control */}
                <input
                  type="range"
                  min="0"
                  max="100"
                  value="66"
                  className="w-full h-3 bg-gray-200 rounded-full appearance-none cursor-pointer"
                />

                <div 
                  className="h-2 bg-gradient-to-r from-gray-200 to-yellow-400 rounded-full"
                  style={{ clipPath: `inset(0 34% 0 0)` }}
                />

                <div className="flex justify-between text-xs text-gray-600">
                  <span>Off</span>
                  <span>Max</span>
                </div>

                {/* Preset buttons */}
                <div>
                  <p className="text-xs font-medium text-gray-700 mb-1">Presets</p>
                  <div className="grid grid-cols-4 gap-1">
                    <button className="py-2 text-xs rounded-md bg-gray-100">Morning</button>
                    <button className="py-2 text-xs rounded-md bg-gray-100">Day</button>
                    <button className="py-2 text-xs rounded-md bg-gray-100">Evening</button>
                    <button className="py-2 text-xs rounded-md bg-gray-100">Night</button>
                  </div>
                </div>

                {/* Save button */}
                <button className="w-full py-2 bg-blue-500 text-white rounded-lg font-medium text-sm">
                  Apply Changes
                </button>
              </div>
            </div>

            {/* Right Side Column - Thermostat and Weather stacked vertically */}
            <div className="space-y-4">
              {/* Thermostat Control - Compact Version */}
              <div className="bg-white p-4 rounded-xl shadow-lg border border-gray-200">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-xl font-semibold text-gray-800">Thermostat</h2>
                  <svg className="w-6 h-6 text-blue-500" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M12 2v10m0 4v6M7 7l5-5 5 5M7 17l5 5 5-5" />
                  </svg>
                </div>

                <div className="flex items-center justify-center gap-6">
                  <button className="p-3 bg-gray-100 rounded-full">
                    <svg className="w-6 h-6" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <path d="M19 12H5M5 12l7 7m-7-7l7-7" />
                    </svg>
                  </button>

                  <div className="text-center">
                    <div className="text-5xl font-bold text-blue-500">25</div>
                    <div className="text-xl text-gray-500">°F</div>
                  </div>

                  <button className="p-3 bg-gray-100 rounded-full">
                    <svg className="w-6 h-6" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <path d="M5 12h14m-7-7l7 7-7 7" />
                    </svg>
                  </button>
                </div>

                <div className="flex justify-between text-sm text-gray-600 mt-4">
                  <span>Min: 50°F</span>
                  <span>Max: 90°F</span>
                </div>
              </div>

              {/* Weather Widget - Below Thermostat */}
              <WeatherWidget weather={user.weather} />
            </div>
          </div>
        </>
      )}

      {/* Device Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
        {filteredDevices.length > 0 ? (
          filteredDevices.map((device) => (
            <DeviceCard 
              key={device.id} 
              device={device} 
              onClick={setSelectedDevice} 
            />
          ))
        ) : (
          <div className="col-span-full text-center py-8">
            <p className="text-gray-500 text-lg">No devices found.</p>
          </div>
        )}
      </div>

      {/* Energy Analytics Popup */}
      {showEnergyPopup && (
        <HomeEnergyPopup onClose={() => setShowEnergyPopup(false)} />
      )}
    </div>
  );
};

export default Dashboard;
