import React, { useState, useEffect } from 'react';
import { Sun, ChevronDown, CheckCircle } from 'lucide-react';

const LightingControl = ({ intensity = 50, rooms = [], activeRoom = null }) => {
  const [lightValue, setLightValue] = useState(intensity);
  const [isDragging, setIsDragging] = useState(false);
  const [showRoomSelector, setShowRoomSelector] = useState(false);
  const [selectedRoom, setSelectedRoom] = useState(activeRoom);
  const [lightPresets, setLightPresets] = useState([
    { name: "Morning", value: 75 },
    { name: "Day", value: 100 },
    { name: "Evening", value: 50 },
    { name: "Night", value: 15 }
  ]);

  // Default rooms if none provided
  const availableRooms = rooms.length > 0 ? rooms : [
    { id: 'living', name: 'Living Room' },
    { id: 'kitchen', name: 'Kitchen' },
    { id: 'bedroom', name: 'Bedroom' },
    { id: 'bathroom', name: 'Bathroom' },
    { id: 'all', name: 'All Rooms' }
  ];

  useEffect(() => {
    if (!selectedRoom && availableRooms.length > 0) {
      setSelectedRoom(availableRooms[0]);
    }
  }, []);

  const handleSliderChange = (e) => {
    const newValue = Math.min(100, Math.max(0, parseInt(e.target.value)));
    setLightValue(newValue);
  };

  const handleInputChange = (e) => {
    const newValue = Math.min(100, Math.max(0, parseInt(e.target.value) || 0));
    setLightValue(newValue);
  };

  const handleMouseDown = () => setIsDragging(true);
  const handleMouseUp = () => setIsDragging(false);
  const handleTouchStart = () => setIsDragging(true);
  const handleTouchEnd = () => setIsDragging(false);

  const handleRoomSelect = (room) => {
    setSelectedRoom(room);
    setShowRoomSelector(false);
    // Here you would typically fetch the selected room's current light value
    // For demo, we'll just set a random value
    setLightValue(Math.floor(Math.random() * 100));
  };

  const applyPreset = (value) => {
    setLightValue(value);
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg border border-gray-200">
      <div className="flex items-center justify-between mb-4">
        <div className="flex flex-col">
          <h2 className="text-xl font-semibold text-gray-800">Lighting Control</h2>

          {/* Room Selector */}
          <div className="relative mt-1">
            <button 
              onClick={() => setShowRoomSelector(!showRoomSelector)}
              className="flex items-center text-sm text-blue-600 hover:text-blue-800 focus:outline-none"
            >
              {selectedRoom?.name || "Select Room"}
              <ChevronDown size={16} className="ml-1" />
            </button>

            {showRoomSelector && (
              <div className="absolute z-10 mt-1 w-48 bg-white rounded-md shadow-lg border border-gray-200">
                {availableRooms.map((room) => (
                  <button
                    key={room.id}
                    className="w-full px-4 py-3 text-left hover:bg-gray-100 flex items-center justify-between"
                    onClick={() => handleRoomSelect(room)}
                  >
                    {room.name}
                    {selectedRoom?.id === room.id && (
                      <CheckCircle size={16} className="text-green-500" />
                    )}
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>

        <div className="p-2 rounded-full bg-yellow-100 flex items-center justify-center">
          <Sun
            size={28}
            className={`text-yellow-500 transition-opacity ${
              lightValue > 0 ? 'opacity-100' : 'opacity-50'
            }`}
          />
        </div>
      </div>

      <div className="space-y-6">
        {/* Light value display */}
        <div className="flex items-center justify-center">
          <div className="text-4xl font-bold text-gray-800">
            {lightValue}
            <span className="text-xl font-normal text-gray-500">%</span>
          </div>
        </div>

        {/* Slider control */}
        <div className="space-y-4">
          <input
            type="range"
            min="0"
            max="100"
            value={lightValue}
            onChange={handleSliderChange}
            onMouseDown={handleMouseDown}
            onMouseUp={handleMouseUp}
            onTouchStart={handleTouchStart}
            onTouchEnd={handleTouchEnd}
            className="w-full h-4 bg-gray-200 rounded-full appearance-none cursor-pointer"
          />

          <div 
            className="h-3 bg-gradient-to-r from-gray-200 to-yellow-400 rounded-full"
            style={{ clipPath: `inset(0 ${100 - lightValue}% 0 0)` }}
          />

          <div className="flex justify-between text-sm text-gray-600">
            <span>Off</span>
            <span className="font-medium">
              {isDragging && 'Adjusting...'}
            </span>
            <span>Max</span>
          </div>
        </div>

        {/* Preset buttons */}
        <div>
          <h3 className="text-sm font-medium text-gray-700 mb-2">Presets</h3>
          <div className="grid grid-cols-4 gap-2">
            {lightPresets.map((preset) => (
              <button
                key={preset.name}
                onClick={() => applyPreset(preset.value)}
                className="py-2 px-1 text-sm rounded-md bg-gray-100 hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                {preset.name}
              </button>
            ))}
          </div>
        </div>

        {/* Manual input */}
        <div className="flex items-center justify-center gap-2">
          <input
            type="number"
            min="0"
            max="100"
            value={lightValue}
            onChange={handleInputChange}
            className="w-16 p-2 text-center border rounded-md"
          />
          <span className="text-gray-600">%</span>
        </div>

        {/* Save button */}
        <button
          className="w-full py-3 bg-blue-500 hover:bg-blue-600 text-white rounded-lg font-medium transition-colors"
        >
          Apply Changes
        </button>
      </div>
    </div>
  );
};

export default LightingControl;
