import React, { useState, useEffect } from "react";
import { Search, Power, TrendingDown, Home, Filter } from 'lucide-react';
import WeatherWidget from "./WeatherWidget";
import DeviceCard from "./DeviceCard";
import LightingControl from "./LightingControl";
import ThermostatControl from "./ThermostatControl";
import { RoomEnergyCard, HomeEnergyPopup } from "./EnergyAnalytics";

const Dashboard = ({ user, selectedRoom, setSelectedDevice }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [showEnergyPopup, setShowEnergyPopup] = useState(false);
  const [activeTab, setActiveTab] = useState("all");
  const [showFilters, setShowFilters] = useState(true);
  const [isLoading, setIsLoading] = useState(true);

  // Simulate loading on initial render
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 800);
    return () => clearTimeout(timer);
  }, []);

  // Handle keyboard shortcut for search
  useEffect(() => {
    const handleKeyDown = (e) => {
      if ((e.metaKey || e.ctrlKey) && e.key === "/") {
        e.preventDefault();
        document.querySelector('input[type="text"]')?.focus();
      }
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  const getFilteredDevices = () => {
    let devices = user.devices;

    // Apply search filter
    devices = devices.filter(device => 
      device.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Apply room filter
    if (selectedRoom) {
      devices = devices.filter(device => device.room === selectedRoom.name);
    }

    // Apply active/inactive filter
    if (activeTab === "active") {
      devices = devices.filter(device => device.status === "On");
    } else if (activeTab === "inactive") {
      devices = devices.filter(device => device.status !== "On");
    }

    return devices;
  };

  const filteredDevices = getFilteredDevices();

  if (isLoading) {
    return (
      <div className="w-full max-w-7xl mx-auto p-4 space-y-6">
        <div className="animate-pulse flex space-x-4 items-center">
          <div className="h-8 w-8 bg-gray-200 rounded-full"></div>
          <div className="h-8 bg-gray-200 rounded w-1/4"></div>
        </div>
        <div className="animate-pulse grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="md:col-span-2 h-40 bg-gray-200 rounded-lg"></div>
          <div className="h-40 bg-gray-200 rounded-lg"></div>
        </div>
        <div className="animate-pulse grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="h-60 bg-gray-200 rounded-lg"></div>
          <div className="h-60 bg-gray-200 rounded-lg"></div>
          <div className="h-60 bg-gray-200 rounded-lg"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full max-w-7xl mx-auto p-4 space-y-6">
      {/* Top Bar with Room Name */}
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-2">
          {selectedRoom && (
            <button
              onClick={() => window.location.reload()}
              className="p-2 hover:bg-gray-100 rounded-full transition-colors"
              aria-label="Return home"
            >
              <Home className="w-5 h-5" />
            </button>
          )}
          <h1 className="text-2xl font-bold text-gray-800">
            {selectedRoom ? selectedRoom.name : "All Devices"}
          </h1>
        </div>

        <button
          onClick={() => setShowFilters(!showFilters)}
          className="p-2 bg-gray-100 hover:bg-gray-200 rounded-full transition-colors md:hidden"
          aria-label="Toggle filters"
        >
          <Filter className="w-5 h-5" />
        </button>
      </div>

      {/* Filter and Search Row */}
      <div className={`${showFilters ? 'flex' : 'hidden'} md:flex flex-wrap items-center justify-between gap-4 mb-6`}>
        {/* Filter Tabs */}
        <div className="flex-shrink-0 flex space-x-1 bg-gray-100 p-1 rounded-lg">
          <button
            onClick={() => setActiveTab("all")}
            className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
              activeTab === "all" 
                ? "bg-blue-500 text-white" 
                : "bg-white text-gray-800 hover:bg-gray-50"
            }`}
          >
            All Devices
          </button>
          <button
            onClick={() => setActiveTab("active")}
            className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
              activeTab === "active" 
                ? "bg-blue-500 text-white" 
                : "bg-white text-gray-800 hover:bg-gray-50"
            }`}
          >
            Active
          </button>
          <button
            onClick={() => setActiveTab("inactive")}
            className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
              activeTab === "inactive" 
                ? "bg-blue-500 text-white" 
                : "bg-white text-gray-800 hover:bg-gray-50"
            }`}
          >
            Inactive
          </button>
        </div>

        {/* Search Bar */}
        <div className="relative w-full md:w-auto md:flex-1 max-w-md ml-auto">
          <input
            type="text"
            placeholder="Search devices... (Ctrl + /)"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
        </div>
      </div>

      {/* Room Energy Card (when room is selected) */}
      {selectedRoom && (
        <RoomEnergyCard room={selectedRoom} />
      )}

      {/* Main Dashboard Section - Only shown when no room is selected */}
      {!selectedRoom && (
        <>
          {/* Cost Summary Cards in smaller layout */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
            {/* Total Consumption Card - Clickable */}
            <div 
              className="bg-white p-6 rounded-xl shadow-lg border border-gray-200 cursor-pointer hover:shadow-xl transition-shadow"
              onClick={() => setShowEnergyPopup(true)}
            >
              <div className="flex items-center gap-3 mb-3">
                <div className="p-2 bg-blue-50 rounded-full">
                  <Power className="w-6 h-6 text-blue-500" />
                </div>
                <h2 className="text-xl font-semibold">Total Consumption</h2>
              </div>
              <p className="text-3xl font-bold text-blue-500">
                {user.totalConsumption} kWh
              </p>
              <div className="mt-3 flex items-center text-sm text-gray-600">
                <TrendingDown className="w-4 h-4 mr-1" />
                <span>Tap for detailed analytics</span>
              </div>
            </div>

            {/* Cost Summary */}
            <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200">
              <div className="flex items-center gap-3 mb-3">
                <div className="p-2 bg-green-50 rounded-full">
                  <svg className="w-6 h-6 text-green-500" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M12 1v22M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6" />
                  </svg>
                </div>
                <h2 className="text-xl font-semibold">Cost Summary</h2>
              </div>
              <p className="text-3xl font-bold text-green-500">
                ${(user.totalConsumption * 0.12).toFixed(2)}
              </p>
              <div className="mt-3 flex items-center text-sm text-gray-600">
                <span className="flex items-center">
                  <svg className="w-4 h-4 mr-1" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M3 3a1 1 0 011-1h12a1 1 0 011 1v3a1 1 0 01-.293.707L12 11.414V15a1 1 0 01-.293.707l-2 2A1 1 0 018 17v-5.586L3.293 6.707A1 1 0 013 6V3z" clipRule="evenodd" />
                  </svg>
                  Based on $0.12 per kWh
                </span>
              </div>
            </div>
          </div>

          {/* Controls and Weather Section */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
            {/* Use the LightingControl component */}
            <LightingControl 
              intensity={user.lighting?.intensity || 66} 
              rooms={rooms}
            />

            {/* Use the ThermostatControl component */}
            <ThermostatControl 
              temperature={user.thermostat?.temperature || 72}
              unit="Â°F"
              minTemp={50}
              maxTemp={90}
            />
          </div>

          {/* Weather Widget in its own row on mobile, hidden on desktop */}
          <div className="block md:hidden mb-6">
            <WeatherWidget weather={user.weather} />
          </div>
        </>
      )}

      {/* Device Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredDevices.length > 0 ? (
          filteredDevices.map((device) => (
            <DeviceCard 
              key={device.id} 
              device={device} 
              onClick={() => setSelectedDevice(device)} 
            />
          ))
        ) : (
          <div className="col-span-full text-center py-12 bg-white rounded-lg shadow-sm border border-gray-200">
            <div className="text-gray-400 mx-auto w-16 h-16 mb-4">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M10 3H6a2 2 0 00-2 2v14c0 1.1.9 2 2 2h12a2 2 0 002-2V9L14 3H6zm4 0v6h6m-5 6v4m-2-2h4" />
              </svg>
            </div>
            <p className="text-gray-500 text-lg">No devices found</p>
            <p className="text-gray-400 text-sm mt-1">Try adjusting your search or filters</p>
          </div>
        )}
      </div>

      {/* Energy Analytics Popup */}
      {showEnergyPopup && (
        <HomeEnergyPopup onClose={() => setShowEnergyPopup(false)} />
      )}
    </div>
  );
};

