import React from "react";

const weatherIcons = {
  Rainy: "/assets/images/cloudy.png",
  Sunny: "/assets/images/sun.png",
  Snowy: "/assets/images/snowy.png",
  Hail: "/assets/images/hail.png"
};

function WeatherWidget({ weather }) {
  return (
    <div className="bg-blue-200 p-4 rounded-lg shadow flex items-center">
      <img src={weatherIcons[weather.condition] || "/assets/images/cloud.png"} alt="Weather" className="w-12 h-12 mr-4" />
      <div>
        <h2 className="text-lg font-semibold">Weather</h2>
        <p className="text-xl font-bold">{weather.temperature}Â°C</p>
        <p>{weather.condition}</p>
      </div>
    </div>
  );
}

export default WeatherWidget;
