import React from "react";

function DeviceCard({ device, onClick }) {
  return (
    <div
      className="p-4 bg-white rounded shadow cursor-pointer hover:shadow-lg transition"
      onClick={() => onClick(device)}
    >
      <img
        src={device.image}
        alt={device.name}
        className="w-12 h-12 mx-auto mb-2"
      />
      <h3 className="text-lg font-bold text-center">{device.name}</h3>
      <p className={`text-center ${device.status === "On" ? "text-green-500" : "text-red-500"}`}>
        Status: {device.status}
      </p>
      <p className="text-center text-gray-600">Room: {device.room}</p>
    </div>
  );
}

export default DeviceCard;
