import React from "react";
import { Power } from 'lucide-react';

function DeviceCard({ device, onClick }) {
  const isOn = device.status === "On";

  // Get base64 image corresponding to device type
  const getDeviceImage = (deviceType) => {
    switch (deviceType) {
      case "Smart Light":
        return "data:image/svg+xml;base64,";
      case "Television":
        return "data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHZpZXdCb3g9IjAgMCAyNCAyNCIgZmlsbD0ibm9uZSIgc3Ryb2tlPSJjdXJyZW50Q29sb3IiIHN0cm9rZS13aWR0aD0iMiI+PHJlY3QgeD0iMyIgeT0iNyIgd2lkdGg9IjE4IiBoZWlnaHQ9IjEyIiByeD0iMiIgcnk9IjIiLz48cG9seWxpbmUgcG9pbnRzPSIxNyAyIDEyIDcgNyAyIi8+PC9zdmc+";
      case "Fridge":
        return "data:image/svg+xml;base64,";
      default:
        return "data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHZpZXdCb3g9IjAgMCAyNCAyNCIgZmlsbD0ibm9uZSIgc3Ryb2tlPSJjdXJyZW50Q29sb3IiIHN0cm9rZS13aWR0aD0iMiI+PHJlY3QgeD0iNCIgeT0iNCIgd2lkdGg9IjE2IiBoZWlnaHQ9IjE2IiByeD0iMiIvPjxjaXJjbGUgY3g9IjEyIiBjeT0iMTIiIHI9IjMiLz48L3N2Zz4=";
    }
  };

  return (
    <div
      onClick={() => onClick(device)}
      className="bg-white rounded-xl shadow-lg p-6 cursor-pointer transform transition-all hover:scale-105 hover:shadow-xl"
    >
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-3">
          <div className="w-12 h-12 flex items-center justify-center">
            <img
              src={getDeviceImage(device.name)}
              alt={device.name}
              className="w-10 h-10"
              style={{ filter: isOn ? 'none' : 'grayscale(100%)' }}
            />
          </div>
          <div>
            <h3 className="text-lg font-semibold">{device.name}</h3>
            <p className="text-sm text-gray-600">{device.room}</p>
          </div>
        </div>
        <Power 
          className={`w-6 h-6 ${isOn ? 'text-green-500' : 'text-red-500'}`} 
        />
      </div>

      <div className="mt-4 flex justify-between items-center text-sm">
        <div>
          <p className="text-gray-600">Power Usage</p>
          <p className="font-semibold">{device.watts || 0}W</p>
        </div>
        <div className="text-right">
          <p className="text-gray-600">Cost/hr</p>
          <p className="font-semibold">${((device.watts || 0) * 0.12 / 1000).toFixed(2)}</p>
        </div>
      </div>
    </div>
  );
}

export default DeviceCard;
