import React from 'react';

const Header = ({ title }) => {
  return (
    <div className="flex items-center justify-between p-4 border-b bg-white shadow-sm">
      <div className="flex items-center">
        <div className="flex items-center mr-4">
          <img 
            src="/assets/images/pulse_logo.png" 
            alt="Pulse Logo" 
            className="h-8 w-auto mr-2"
            onError={(e) => {
              e.target.style.display = 'none';
              e.target.nextElementSibling.classList.remove('ml-2');
            }}
          />
          <span className="text-2xl font-bold text-brown-700 ml-2">Pulse</span>
        </div>
        {title && <h1 className="text-xl font-semibold text-gray-700">{title}</h1>}
      </div>
      <div className="text-sm text-gray-600">Smart Energy Management System</div>
    </div>
  );
};

export default Header;
