import React, { useState } from "react";
import Sidebar from "./components/Sidebar";
import Dashboard from "./components/Dashboard";
import "./index.css";

function App() {
  const [rooms, setRooms] = useState([
    { name: "Living Room" },
    { name: "Bedroom" },
    { name: "Kitchen" },
    { name: "Dining Room" },
    { name: "Play Room" }
  ]);

  const addRoom = () => {
    const roomName = prompt("Enter the new room name:");
    if (roomName) {
      setRooms([...rooms, { name: roomName }]);
    }
  };

  const user = {
    name: "Albert",
    weather: "Rainy, 19°C",
    humidity: 65,
    totalConsumption: 160,
    lighting: { intensity: "80%" },
    thermostat: { temperature: 25 },
    devices: [
      { name: "Smart Light", status: "On", id: "SL123", cost: 0.5, watts: 10, image: require("./assets/images/lightbulb.png") },
      { name: "Air Conditioner", status: "Off", id: "AC456", cost: 1.5, watts: 2000, image: require("./assets/images/speaker.png") },
      { name: "Door Lock", status: "Locked", id: "DL789", cost: 0, watts: 2, image: require("./assets/images/fridge.png") },
      { name: "Cleaning Vacuum", status: "Off", id: "CV012", cost: 0.3, watts: 500, image: require("./assets/images/dryer.png") },
      { name: "Unknown Device", status: "N/A", id: "UD000", cost: 0, watts: 0, image: require("./assets/images/noniot.png") }
    ]
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar rooms={rooms} addRoom={addRoom} />
      <main className="flex-grow p-6">
        <Dashboard user={user} />
      </main>
    </div>
  );
}

export default App;
