import React from "react";
import { Power } from 'lucide-react';

const deviceImages = {
  "Smart Light": "/api/placeholder/64/64",
  "Television": "/api/placeholder/64/64",
  "Fridge": "/api/placeholder/64/64"
};

function DeviceCard({ device, onClick }) {
  const isOn = device.status === "On";

  return (
    <div
      onClick={() => onClick(device)}
      className="bg-white rounded-xl shadow-lg p-6 cursor-pointer transform transition-all hover:scale-105 hover:shadow-xl"
    >
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-3">
          <img
            src={deviceImages[device.name] || "/api/placeholder/64/64"}
            alt={device.name}
            className="w-12 h-12 rounded-lg object-cover"
          />
          <div>
            <h3 className="text-lg font-semibold">{device.name}</h3>
            <p className="text-sm text-gray-600">{device.room}</p>
          </div>
        </div>
        <Power 
          className={`w-6 h-6 ${isOn ? 'text-green-500' : 'text-red-500'}`} 
        />
      </div>

      <div className="mt-4 flex justify-between items-center text-sm">
        <div>
          <p className="text-gray-600">Power Usage</p>
          <p className="font-semibold">{device.watts || 0}W</p>
        </div>
        <div className="text-right">
          <p className="text-gray-600">Cost/hr</p>
          <p className="font-semibold">${((device.watts || 0) * 0.12 / 1000).toFixed(2)}</p>
        </div>
      </div>
    </div>
  );
}

export default DeviceCard;
