import React, { useState } from "react";
import { Search, Power, TrendingDown, ChevronDown, Home } from 'lucide-react';
import WeatherWidget from "./WeatherWidget";
import DeviceCard from "./DeviceCard";
import LightingControl from "./LightingControl";
import ThermostatControl from "./ThermostatControl";
import { RoomEnergyCard, HomeEnergyPopup } from "./EnergyAnalytics";

const Dashboard = ({ user, selectedRoom, setSelectedDevice }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [showEnergyPopup, setShowEnergyPopup] = useState(false);
  const [activeTab, setActiveTab] = useState("all");

  const getFilteredDevices = () => {
    let devices = user.devices;

    // Apply search filter
    devices = devices.filter(device => 
      device.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Apply room filter
    if (selectedRoom) {
      devices = devices.filter(device => device.room === selectedRoom.name);
    }

    // Apply active/inactive filter
    if (activeTab === "active") {
      devices = devices.filter(device => device.status === "On");
    } else if (activeTab === "inactive") {
      devices = devices.filter(device => device.status !== "On");
    }

    return devices;
  };

  const filteredDevices = getFilteredDevices();

  return (
    <div className="w-full max-w-7xl mx-auto space-y-6">
      {/* Top Section with Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="md:col-span-2">
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="grid grid-cols-3 gap-4">
              <div className="bg-gray-50 rounded-lg p-4">
                <p className="text-sm text-gray-600">Active Devices</p>
                <p className="text-2xl font-bold text-blue-600">
                  {user.devices.filter(d => d.status === "On").length}
                </p>
              </div>
              <div className="bg-gray-50 rounded-lg p-4">
                <p className="text-sm text-gray-600">Total Power</p>
                <p className="text-2xl font-bold text-green-600">
                  {user.totalConsumption}W
                </p>
              </div>
              <div className="bg-gray-50 rounded-lg p-4">
                <p className="text-sm text-gray-600">Efficiency</p>
                <p className="text-2xl font-bold text-purple-600">92%</p>
              </div>
            </div>
          </div>
        </div>
        <div>
          <WeatherWidget weather={user.weather} />
        </div>
      </div>

      {/* Devices Section */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <div className="flex flex-wrap items-center gap-4 mb-6">
          {/* Filter Tabs and Search Bar */}
          <div className="flex items-center gap-4 w-full">
            <div className="flex bg-gray-100 rounded-lg p-1">
              <button
                onClick={() => setActiveTab("all")}
                className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                  activeTab === "all"
                    ? "bg-white text-blue-600 shadow-sm"
                    : "text-gray-600 hover:text-gray-900"
                }`}
              >
                All Devices
              </button>
              <button
                onClick={() => setActiveTab("active")}
                className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                  activeTab === "active"
                    ? "bg-white text-blue-600 shadow-sm"
                    : "text-gray-600 hover:text-gray-900"
                }`}
              >
                Active
              </button>
              <button
                onClick={() => setActiveTab("inactive")}
                className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                  activeTab === "inactive"
                    ? "bg-white text-blue-600 shadow-sm"
                    : "text-gray-600 hover:text-gray-900"
                }`}
              >
                Inactive
              </button>
            </div>

            <div className="flex-1 relative">
              <input
                type="text"
                placeholder="Search devices..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
            </div>
          </div>
        </div>

        {/* Devices Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredDevices.map((device) => (
            <DeviceCard
              key={device.id}
              device={device}
              onClick={() => setSelectedDevice(device)}
            />
          ))}
          {filteredDevices.length === 0 && (
            <div className="col-span-full text-center py-12">
              <p className="text-gray-500">No devices found</p>
            </div>
          )}
        </div>
      </div>

      {/* Controls Section */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <LightingControl intensity={user.lighting.intensity} />
        <ThermostatControl temperature={user.thermostat.temperature} />
      </div>

      {/* Energy Analytics */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <RoomEnergyCard onClick={() => setShowEnergyPopup(true)} />
      </div>

      {/* Energy Popup */}
      {showEnergyPopup && (
        <HomeEnergyPopup onClose={() => setShowEnergyPopup(false)} />
      )}
    </div>
  );
};

export default Dashboard;
