import React, { useState, useRef, useEffect } from "react";
import { Search, Home, Power, TrendingDown, X, Filter } from 'lucide-react';
import WeatherWidget from "./WeatherWidget";
import DeviceCard from "./DeviceCard";
import LightingControl from "./LightingControl";
import ThermostatControl from "./ThermostatControl";
import { RoomEnergyCard, HomeEnergyPopup } from "./EnergyAnalytics";

const Dashboard = ({ user, selectedRoom, setSelectedDevice }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [showEnergyPopup, setShowEnergyPopup] = useState(false);
  const [showSearch, setShowSearch] = useState(false);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [activeFilter, setActiveFilter] = useState("all");
  const searchInputRef = useRef(null);
  const pullStartY = useRef(null);

  // Handle pull to refresh
  const handleTouchStart = (e) => {
    const scrollTop = document.documentElement.scrollTop || document.body.scrollTop;
    if (scrollTop === 0) {
      pullStartY.current = e.touches[0].clientY;
    }
  };

  const handleTouchMove = (e) => {
    if (!pullStartY.current) return;

    const pullMoveY = e.touches[0].clientY;
    const pullDistance = pullMoveY - pullStartY.current;

    // If pulled down at least 60px while at the top
    if (pullDistance > 60) {
      setIsRefreshing(true);
    }
  };

  const handleTouchEnd = () => {
    if (isRefreshing) {
      // Simulate refresh
      setTimeout(() => {
        setIsRefreshing(false);
        // Here you would fetch new data
        console.log("Refreshing data...");
      }, 1000);
    }
    pullStartY.current = null;
  };

  // Focus search input when search is shown
  useEffect(() => {
    if (showSearch && searchInputRef.current) {
      searchInputRef.current.focus();
    }
  }, [showSearch]);

  // Filter devices based on search term and room
  const filteredDevices = selectedRoom
    ? user.devices.filter((device) => {
        const matchesRoom = device.room === selectedRoom.name;
        const matchesSearch = device.name.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesFilter = activeFilter === "all" || 
                              (activeFilter === "on" && device.status === "On") ||
                              (activeFilter === "off" && device.status !== "On");
        return matchesRoom && matchesSearch && matchesFilter;
      })
    : user.devices.filter((device) => {
        const matchesSearch = device.name.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesFilter = activeFilter === "all" || 
                              (activeFilter === "on" && device.status === "On") ||
                              (activeFilter === "off" && device.status !== "On");
        return matchesSearch && matchesFilter;
      });

  return (
    <div 
      className="w-full max-w-7xl mx-auto p-4 md:p-6 space-y-6"
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      onTouchEnd={handleTouchEnd}
    >
      {/* Pull to Refresh Indicator */}
      {isRefreshing && (
        <div className="fixed top-0 left-0 right-0 bg-blue-500 text-white text-center py-2 z-50">
          Refreshing...
        </div>
      )}

      {/* Top Bar */}
      <div className="flex items-center justify-between gap-4 mb-6">
        <div className="flex items-center gap-3">
          {selectedRoom && (
            <button
              onClick={() => window.location.reload()}
              className="p-3 hover:bg-gray-100 rounded-full transition-colors"
              aria-label="Go to home"
            >
              <Home className="w-6 h-6" />
            </button>
          )}
          <h1 className="text-2xl md:text-3xl font-bold text-gray-800">
            {selectedRoom ? selectedRoom.name : `Welcome, ${user.name}`}
          </h1>
        </div>

        {/* Search toggle button */}
        {!showSearch ? (
          <button
            onClick={() => setShowSearch(true)}
            className="p-3 bg-gray-100 rounded-full hover:bg-gray-200 transition-colors"
            aria-label="Search devices"
          >
            <Search className="w-6 h-6 text-gray-600" />
          </button>
        ) : (
          <div className="flex-1 flex items-center">
            <div className="relative flex-1">
              <input
                ref={searchInputRef}
                type="text"
                placeholder="Search devices..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-10 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 text-lg"
              />
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
              {searchTerm && (
                <button 
                  className="absolute right-3 top-1/2 transform -translate-y-1/2"
                  onClick={() => setSearchTerm("")}
                >
                  <X className="w-5 h-5 text-gray-400" />
                </button>
              )}
            </div>
            <button
              onClick={() => setShowSearch(false)}
              className="ml-2 p-3 bg-gray-100 rounded-full hover:bg-gray-200 transition-colors"
            >
              <X className="w-6 h-6 text-gray-600" />
            </button>
          </div>
        )}
      </div>

      {/* Filter Buttons */}
      <div className="flex overflow-x-auto pb-2 gap-2 scrollbar-hide">
        <button
          className={`px-4 py-2 rounded-full text-lg whitespace-nowrap ${
            activeFilter === "all" ? "bg-blue-500 text-white" : "bg-gray-100"
          }`}
          onClick={() => setActiveFilter("all")}
        >
          All Devices
        </button>
        <button
          className={`px-4 py-2 rounded-full text-lg whitespace-nowrap ${
            activeFilter === "on" ? "bg-green-500 text-white" : "bg-gray-100"
          }`}
          onClick={() => setActiveFilter("on")}
        >
          Active Devices
        </button>
        <button
          className={`px-4 py-2 rounded-full text-lg whitespace-nowrap ${
            activeFilter === "off" ? "bg-red-500 text-white" : "bg-gray-100"
          }`}
          onClick={() => setActiveFilter("off")}
        >
          Inactive Devices
        </button>
      </div>

      {/* Room Energy Card (when room is selected) */}
      {selectedRoom && (
        <RoomEnergyCard room={selectedRoom} />
      )}

      {/* Main Dashboard Widgets */}
      {!selectedRoom && (
        <>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 md:gap-6 mb-6">
            {/* Weather */}
            <WeatherWidget weather={user.weather} />

            {/* Total Consumption Card - Clickable */}
            <div 
              className="bg-white p-6 rounded-xl shadow-lg border border-gray-200 cursor-pointer hover:shadow-xl transition-shadow active:bg-gray-50"
              onClick={() => setShowEnergyPopup(true)}
            >
              <div className="flex items-center gap-3 mb-4">
                <Power className="w-8 h-8 text-blue-500" />
                <h2 className="text-xl font-semibold">Total Consumption</h2>
              </div>
              <p className="text-3xl font-bold text-blue-500">
                {user.totalConsumption} kWh
              </p>
              <div className="mt-4 flex items-center text-sm text-gray-600">
                <TrendingDown className="w-5 h-5 mr-2" />
                <span>Tap for detailed analytics</span>
              </div>
            </div>

            {/* Cost Summary */}
            <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200">
              <h2 className="text-xl font-semibold mb-4">Cost Summary</h2>
              <p className="text-3xl font-bold text-green-500">
                ${(user.totalConsumption * 0.12).toFixed(2)}
              </p>
              <div className="flex items-center mt-4 text-sm text-gray-600">
                <Filter className="w-5 h-5 mr-2" />
                <span>Based on $0.12 per kWh</span>
              </div>
            </div>
          </div>

          {/* Controls Section */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 md:gap-6 mb-6">
            <LightingControl intensity={user.lighting.intensity} />
            <ThermostatControl temperature={user.thermostat.temperature} />
          </div>
        </>
      )}

      {/* Devices Section Title */}
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-semibold">
          {filteredDevices.length > 0 ? `Devices (${filteredDevices.length})` : "No Devices Found"}
        </h2>
      </div>

      {/* Device Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6">
        {filteredDevices.length > 0 ? (
          filteredDevices.map((device) => (
            <DeviceCard 
              key={device.id} 
              device={device} 
              onClick={setSelectedDevice} 
            />
          ))
        ) : (
          <div className="col-span-full bg-gray-50 rounded-xl p-8 text-center">
            <p className="text-gray-500 text-lg">No devices match your filters.</p>
            {searchTerm && (
              <button 
                className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-lg"
                onClick={() => setSearchTerm("")}
              >
                Clear Search
              </button>
            )}
          </div>
        )}
      </div>

      {/* Energy Analytics Popup */}
      {showEnergyPopup && (
        <HomeEnergyPopup onClose={() => setShowEnergyPopup(false)} />
      )}
    </div>
  );
};

export default Dashboard;
