import React, { useState } from "react";

function AccountSettings({ setShowAccountSettings, userData = {
  account: {
    firstName: "",
    lastName: "",
    emails: [""],
    phoneNumbers: [""],
    address: {
      line1: "",
      line2: "",
      city: "",
      state: "",
      zipCode: ""
    }
  },
  devices: []
} }) {
  const [selectedTimes, setSelectedTimes] = useState({});
  const [isSelecting, setIsSelecting] = useState(false);
  const [selectionStart, setSelectionStart] = useState(null);
  const [currentSelection, setCurrentSelection] = useState(null);
  const [selectionValue, setSelectionValue] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [alwaysOnDevices, setAlwaysOnDevices] = useState([]);
  const [searchType, setSearchType] = useState("name");

  const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
  const hours = Array.from({ length: 24 }, (_, i) => `${i}:00`);

  const filteredDevices = userData.devices.filter(device => {
    if (!searchTerm) return true;
    const isNotAlreadySelected = !alwaysOnDevices.some(d => d.id === device.id);
    return searchType === "id"
      ? device.id.toString() === searchTerm && isNotAlreadySelected
      : device.name.toLowerCase().includes(searchTerm.toLowerCase()) && isNotAlreadySelected;
  });

  const addDevice = (device) => {
    setAlwaysOnDevices(prev => [...prev, device]);
    setSearchTerm("");
  };

  const removeDevice = (deviceId) => {
    setAlwaysOnDevices(prev => prev.filter(d => d.id !== deviceId));
  };

  return (
    <div className="p-6 bg-white rounded shadow-md overflow-y-auto h-screen">
      <button className="bg-gray-500 text-white p-2 rounded mb-4" onClick={() => setShowAccountSettings(false)}>
        Back
      </button>
      <h2 className="text-2xl font-bold mb-4">Account Settings</h2>
      <div className="grid grid-cols-2 gap-4">
        <div>
          <label>First Name:</label>
          <input type="text" className="p-2 border rounded w-full mb-2" defaultValue={userData.account.firstName} />
          <label>Last Name:</label>
          <input type="text" className="p-2 border rounded w-full mb-2" defaultValue={userData.account.lastName} />
        </div>
      </div>

      <h3 className="text-xl font-semibold mt-6 mb-2">Work Schedule</h3>
      <div className="overflow-auto bg-gray-200 p-4 rounded-lg">
        <p className="mb-2">Click and drag to select/unselect multiple hours when you are not home.</p>
        <div className="grid grid-cols-8 gap-2">
          <div></div>
          {daysOfWeek.map((day, index) => (
            <div key={index} className="text-center font-bold">{day}</div>
          ))}
        </div>
      </div>

      <h3 className="text-xl font-semibold mt-6 mb-2">Always-On Devices:</h3>
      <div className="grid grid-cols-2 gap-4 bg-gray-200 p-4 rounded-lg">
        <div>
          <div className="mb-4 flex gap-2">
            <select className="p-2 border rounded" value={searchType} onChange={(e) => setSearchType(e.target.value)}>
              <option value="name">Search by Name</option>
              <option value="id">Search by ID</option>
            </select>
            <input
              type="text"
              placeholder={searchType === "id" ? "Enter device ID..." : "Search devices..."}
              className="p-2 border rounded w-full"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <div className="bg-white rounded-lg p-2 max-h-64 overflow-y-auto">
            {filteredDevices.map(device => (
              <div key={device.id} className="flex justify-between items-center p-2 hover:bg-gray-100 rounded">
                <div>
                  <p className="font-semibold">{device.name} (ID: {device.id})</p>
                  <p className="text-sm text-gray-600">{device.room}</p>
                </div>
                <button onClick={() => addDevice(device)} className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600">
                  Add
                </button>
              </div>
            ))}
            {searchTerm && filteredDevices.length === 0 && (
              <p className="text-gray-500 text-center p-2">No devices found</p>
            )}
          </div>
        </div>
      </div>
      <button className="bg-green-500 text-white p-2 rounded mt-4 w-full" onClick={() => {
        console.log("Saving changes", { alwaysOnDevices });
      }}>
        Save Changes
      </button>
    </div>
  );
}

export default AccountSettings;
