import React from "react";

function Sidebar({ rooms, addRoom }) {
  return (
    <div className="w-1/8 h-full bg-gray-900 text-white p-5 flex flex-col justify-between">
      <div>
        <h2 className="text-xl font-bold mb-4">Smart Home</h2>
        <ul>
          {rooms.map((room, index) => (
            <li key={index} className="mb-2 cursor-pointer hover:underline">
              {room.name}
            </li>
          ))}
          <li className="cursor-pointer text-blue-400 hover:text-blue-300 mt-2" onClick={addRoom}>+ Add Room</li>
        </ul>
      </div>
      <div className="cursor-pointer text-gray-400 hover:text-white mt-auto">
        Account Settings
      </div>
    </div>
  );
}

export default Sidebar;
