import React from "react";

const Sidebar = ({ rooms, addRoom, setSelectedRoom, setShowAccountSettings, goHome }) => {
  return (
    <div className="w-1/6 bg-gray-900 text-white h-screen flex flex-col">
      <h2 className="text-xl font-bold p-4">Smart Home</h2>
      <nav className="flex-grow">
        <button
          className="block px-4 py-2 text-left w-full hover:bg-gray-700 font-bold"
          onClick={goHome}
        >
          üè† Home
        </button>
        {rooms.map((room, index) => (
          <button
            key={index}
            className="block px-4 py-2 text-left w-full hover:bg-gray-700"
            onClick={() => setSelectedRoom(room)}
          >
            {room.name}
          </button>
        ))}
        <button
          className="block px-4 py-2 text-left text-blue-400 hover:text-blue-600"
          onClick={addRoom}
        >
          + Add Room
        </button>
      </nav>
      <button
        className="p-4 bg-gray-800 text-white w-full hover:bg-gray-700"
        onClick={setShowAccountSettings}
      >
        ‚öôÔ∏è Account Settings
      </button>
    </div>
  );
};

export default Sidebar;
