import React, { useState, useEffect } from "react";
import { X, Save, ChevronLeft, ChevronRight } from "lucide-react";
import lightbulb from "../assets/images/lightbulb.png";
import noniot from "../assets/images/noniot.png";
import television from "../assets/images/television.png";
import fridge from "../assets/images/fridge.png";

const deviceImages = {
  "Smart Light": lightbulb,
  "Television": television,
  "Fridge": fridge
};

function DevicePopup({ device = {}, setSelectedDevice, rooms = [] }) {
  const {
    name = "",
    id = "",
    watts = 0,
    cost = 0,
    room = "",
    status = "",
  } = device;

  const [deviceName, setDeviceName] = useState(name);
  const [selectedRoom, setSelectedRoom] = useState(room);
  const [activeTab, setActiveTab] = useState("details");
  const [timeRange, setTimeRange] = useState("day");
  const [notes, setNotes] = useState("");
  const [hasChanges, setHasChanges] = useState(false);
  const [showConfirmation, setShowConfirmation] = useState(false);

  // Track changes
  useEffect(() => {
    if (deviceName !== name || selectedRoom !== room || notes !== "") {
      setHasChanges(true);
    } else {
      setHasChanges(false);
    }
  }, [deviceName, selectedRoom, notes, name, room]);

  // Handle swipe gestures for tab navigation
  const [touchStart, setTouchStart] = useState(null);
  const [touchEnd, setTouchEnd] = useState(null);

  const minSwipeDistance = 50;

  const onTouchStart = (e) => {
    setTouchEnd(null);
    setTouchStart(e.targetTouches[0].clientX);
  };

  const onTouchMove = (e) => {
    setTouchEnd(e.targetTouches[0].clientX);
  };

  const onTouchEnd = () => {
    if (!touchStart || !touchEnd) return;

    const distance = touchStart - touchEnd;
    const isLeftSwipe = distance > minSwipeDistance;
    const isRightSwipe = distance < -minSwipeDistance;

    if (isLeftSwipe && activeTab === "details") {
      setActiveTab("usage");
    } else if (isRightSwipe && activeTab === "usage") {
      setActiveTab("details");
    }
  };

  const handleClose = () => {
    if (hasChanges) {
      setShowConfirmation(true);
    } else {
      setSelectedDevice(null);
    }
  };

  const handleSave = () => {
    console.log("Saving device changes:", {
      deviceName,
      selectedRoom,
      notes,
    });
    // Close the popup after saving
    setSelectedDevice(null);
  };

  const ConfirmationDialog = () => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md mx-4">
        <h3 className="text-xl font-bold mb-4">Unsaved Changes</h3>
        <p className="mb-6">You have unsaved changes. Do you want to save them before closing?</p>
        <div className="flex justify-end gap-4">
          <button
            className="px-6 py-3 bg-gray-200 text-gray-800 rounded-lg text-lg font-medium hover:bg-gray-300"
            onClick={() => setSelectedDevice(null)}
          >
            Discard
          </button>
          <button
            className="px-6 py-3 bg-blue-500 text-white rounded-lg text-lg font-medium hover:bg-blue-600"
            onClick={handleSave}
          >
            Save
          </button>
        </div>
      </div>
    </div>
  );

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-40">
      <div 
        className="bg-white rounded-xl shadow-2xl w-full max-w-3xl mx-4 overflow-hidden"
        onTouchStart={onTouchStart}
        onTouchMove={onTouchMove}
        onTouchEnd={onTouchEnd}
      >
        {/* Header */}
        <div className="flex justify-between items-center p-6 border-b">
          <h2 className="text-2xl font-bold">{name}</h2>
          <button
            onClick={handleClose}
            className="p-3 hover:bg-gray-100 rounded-full"
            aria-label="Close popup"
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        {/* Device Info */}
        <div className="flex items-start p-6">
          <div className="w-32 h-32 bg-gray-50 rounded-xl flex items-center justify-center mr-6">
            <img 
              src={deviceImages[name] || noniot}
              alt={name} 
              className="w-24 h-24 object-contain"
            />
          </div>
          <div>
            <p className="text-gray-600 mb-2">ID: {id}</p>
            <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
              status === "On" ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
            }`}>
              {status === "On" ? 'Active' : 'Inactive'}
            </div>
          </div>
        </div>

        {/* Tabs */}
        <div className="px-6">
          <div className="flex border-b mb-4">
            <button
              className={`px-6 py-4 text-lg font-medium ${
                activeTab === "details"
                  ? "border-b-2 border-blue-500 text-blue-500"
                  : "text-gray-500"
              }`}
              onClick={() => setActiveTab("details")}
            >
              Device Details
            </button>
            <button
              className={`px-6 py-4 text-lg font-medium ${
                activeTab === "usage"
                  ? "border-b-2 border-blue-500 text-blue-500"
                  : "text-gray-500"
              }`}
              onClick={() => setActiveTab("usage")}
            >
              Usage Analytics
            </button>
          </div>

          {/* Swipe indicator */}
          <div className="flex justify-center mb-2">
            <div className="flex items-center text-gray-400 text-sm">
              <ChevronLeft className="w-4 h-4" />
              <span className="mx-1">Swipe to navigate</span>
              <ChevronRight className="w-4 h-4" />
            </div>
          </div>

          {/* Details Tab Content */}
          {activeTab === "details" && (
            <div className="space-y-6 pb-6">
              <div>
                <label className="block text-lg font-medium mb-2">Device Name</label>
                <input
                  type="text"
                  className="p-4 border rounded-lg w-full text-lg"
                  value={deviceName}
                  onChange={(e) => setDeviceName(e.target.value)}
                />
              </div>

              <div className="grid grid-cols-2 gap-6">
                <div className="bg-gray-50 p-4 rounded-lg">
                  <label className="block text-lg font-medium mb-1">Power Usage</label>
                  <p className="text-xl font-bold">{watts} W</p>
                </div>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <label className="block text-lg font-medium mb-1">Cost per kWh</label>
                  <p className="text-xl font-bold">${cost.toFixed(2)}</p>
                </div>
              </div>

              <div>
                <label className="block text-lg font-medium mb-2">Room</label>
                <select 
                  className="p-4 border rounded-lg w-full text-lg appearance-none bg-white"
                  value={selectedRoom}
                  onChange={(e) => setSelectedRoom(e.target.value)}
                >
                  {rooms.map((room, index) => (
                    <option key={index} value={room.name || ""}>
                      {room.name || "Unknown Room"}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-lg font-medium mb-2">Notes</label>
                <textarea
                  className="p-4 border rounded-lg w-full text-lg"
                  rows={4}
                  value={notes}
                  onChange={(e) => setNotes(e.target.value)}
                  placeholder="Add notes about this device..."
                />
              </div>
            </div>
          )}

          {/* Usage Tab Content */}
          {activeTab === "usage" && (
            <div className="pb-6">
              <div className="mb-6">
                <div className="flex flex-wrap gap-2 my-4">
                  {["day", "week", "month", "year"].map((range) => (
                    <button
                      key={range}
                      onClick={() => setTimeRange(range)}
                      className={`px-4 py-3 rounded-lg text-lg ${
                        timeRange === range
                          ? "bg-blue-500 text-white"
                          : "bg-gray-100 text-gray-800"
                      }`}
                    >
                      {range.charAt(0).toUpperCase() + range.slice(1)}
                    </button>
                  ))}
                </div>
                <div className="h-64 bg-gray-50 rounded-lg flex flex-col items-center justify-center">
                  <p className="text-gray-500 text-lg mb-2">Usage chart will be displayed here</p>
                  <p className="text-gray-400">Data for {timeRange} view</p>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-6 mt-6">
                <div className="bg-gray-50 p-4 rounded-lg text-center">
                  <p className="text-gray-600 mb-1">Average Usage</p>
                  <p className="text-2xl font-bold">{Math.round(watts * 0.85)} W</p>
                </div>
                <div className="bg-gray-50 p-4 rounded-lg text-center">
                  <p className="text-gray-600 mb-1">Total Cost ({timeRange})</p>
                  <p className="text-2xl font-bold">
                    ${((watts * 0.12 * (timeRange === "day" ? 8 : timeRange === "week" ? 40 : timeRange === "month" ? 180 : 2000)) / 1000).toFixed(2)}
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Action Buttons */}
        <div className="p-6 border-t bg-gray-50 flex justify-end gap-4">
          <button
            className="bg-gray-200 text-gray-800 px-6 py-3 rounded-lg text-lg font-medium hover:bg-gray-300 flex items-center gap-2"
            onClick={handleClose}
          >
            Cancel
          </button>
          <button
            className={`px-6 py-3 rounded-lg text-lg font-medium flex items-center gap-2 ${
              hasChanges 
                ? "bg-blue-500 text-white hover:bg-blue-600" 
                : "bg-gray-300 text-gray-500"
            }`}
            onClick={handleSave}
            disabled={!hasChanges}
          >
            <Save className="w-5 h-5" />
            Save
          </button>
        </div>
      </div>

      {/* Confirmation Dialog */}
      {showConfirmation && <ConfirmationDialog />}
    </div>
  );
}

export default DevicePopup;
