import React, { useState } from "react";

function AccountSettings({ setShowAccountSettings }) {
  const [selectedTimes, setSelectedTimes] = useState({});

  const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
  const hours = Array.from({ length: 24 }, (_, i) => `${i}:00`);

  // Function to toggle multiple time slots
  const toggleTimeSlot = (day, hour) => {
    setSelectedTimes((prev) => {
      const updated = { ...prev };
      if (!updated[day]) updated[day] = {}; // Initialize if empty
      updated[day][hour] = !updated[day][hour]; // Toggle selection
      return { ...updated };
    });
  };

  return (
    <div className="p-6 bg-white rounded shadow-md overflow-y-auto h-screen">
      {/* Back Button */}
      <button
        className="bg-gray-500 text-white p-2 rounded mb-4"
        onClick={() => setShowAccountSettings(false)}
      >
        Back
      </button>

      {/* Header */}
      <h2 className="text-2xl font-bold mb-4">Account Settings</h2>

      {/* Personal Information */}
      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block mb-2">First Name:</label>
          <input type="text" className="p-2 border rounded w-full mb-2" />

          <label className="block mb-2">Last Name:</label>
          <input type="text" className="p-2 border rounded w-full mb-2" />

          <label className="block mb-2">Email:</label>
          <input type="email" className="p-2 border rounded w-full mb-2" />

          <label className="block mb-2">Phone Number:</label>
          <input type="text" className="p-2 border rounded w-full mb-2" />
        </div>

        {/* Address Section */}
        <div>
          <label className="block mb-2">Address Line 1:</label>
          <input type="text" className="p-2 border rounded w-full mb-2" />

          <label className="block mb-2">Address Line 2:</label>
          <input type="text" className="p-2 border rounded w-full mb-2" />

          <div className="grid grid-cols-3 gap-2">
            <div>
              <label className="block mb-2">City:</label>
              <input type="text" className="p-2 border rounded w-full mb-2" />
            </div>
            <div>
              <label className="block mb-2">State:</label>
              <input type="text" className="p-2 border rounded w-full mb-2" />
            </div>
            <div>
              <label className="block mb-2">Zip Code:</label>
              <input type="text" className="p-2 border rounded w-full mb-2" />
            </div>
          </div>
        </div>
      </div>

      {/* Work Schedule Table */}
      <h3 className="text-xl font-semibold mt-6 mb-2">Work Schedule (Click to Select Multiple):</h3>
      <div className="overflow-auto bg-gray-200 p-4 rounded-lg">
        <p className="mb-2">Click to select/unselect multiple hours when you are not home.</p>
        <div className="grid grid-cols-8 gap-2">
          <div></div> {/* Empty space for alignment */}
          {daysOfWeek.map((day, index) => (
            <div key={index} className="text-center font-bold">{day}</div>
          ))}
        </div>

        {hours.map((hour, rowIndex) => (
          <div key={rowIndex} className="grid grid-cols-8 gap-2 mt-1">
            <div className="font-bold text-center">{hour}</div>
            {daysOfWeek.map((day, colIndex) => (
              <div
                key={`${day}-${hour}`} // Unique key for each slot
                className={`border p-2 text-center cursor-pointer ${
                  selectedTimes[day]?.[hour] ? "bg-red-500 text-white" : "hover:bg-gray-300"
                }`}
                onClick={() => toggleTimeSlot(day, hour)}
              >
                {hour}
              </div>
            ))}
          </div>
        ))}
      </div>

      {/* Save Button */}
      <button className="bg-green-500 text-white p-2 rounded mt-4 w-full">Save</button>
    </div>
  );
}

export default AccountSettings;
