import React, { useState } from "react";
import { Search, Home, Battery, Zap, Gauge, ArrowUpRight, ArrowDownRight, TrendingUp, Timer } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

// Mock data - replace with real data in production
const mockUsageData = {
  house: [
    { name: 'Mon', watts: 420 },
    { name: 'Tue', watts: 380 },
    { name: 'Wed', watts: 450 },
    { name: 'Thu', watts: 400 },
    { name: 'Fri', watts: 480 },
    { name: 'Sat', watts: 520 },
    { name: 'Sun', watts: 490 }
  ],
  rooms: {
    "Living Room": [
      { name: 'Mon', watts: 120 },
      { name: 'Tue', watts: 110 },
      { name: 'Wed', watts: 130 },
      { name: 'Thu', watts: 115 },
      { name: 'Fri', watts: 140 },
      { name: 'Sat', watts: 150 },
      { name: 'Sun', watts: 135 }
    ],
    // Add data for other rooms...
  }
};

const AnalyticsCard = ({ title, value, trend, unit, icon: Icon }) => (
  <Card>
    <CardContent className="p-6">
      <div className="flex items-center justify-between">
        <div className="space-y-2">
          <p className="text-sm text-gray-500">{title}</p>
          <div className="flex items-center space-x-2">
            <span className="text-2xl font-bold">{value}</span>
            <span className="text-sm text-gray-500">{unit}</span>
          </div>
        </div>
        <div className={`p-3 rounded-full ${trend > 0 ? 'bg-red-100' : 'bg-green-100'}`}>
          <Icon className={`w-5 h-5 ${trend > 0 ? 'text-red-600' : 'text-green-600'}`} />
        </div>
      </div>
      <div className="flex items-center mt-2">
        {trend > 0 ? <ArrowUpRight className="w-4 h-4 text-red-600" /> : <ArrowDownRight className="w-4 h-4 text-green-600" />}
        <span className={`text-sm ${trend > 0 ? 'text-red-600' : 'text-green-600'}`}>
          {Math.abs(trend)}% from last period
        </span>
      </div>
    </CardContent>
  </Card>
);

const UsageChart = ({ data, title }) => (
  <Card className="col-span-3">
    <CardHeader>
      <CardTitle>{title}</CardTitle>
    </CardHeader>
    <CardContent>
      <div className="h-[300px]">
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={data} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Line type="monotone" dataKey="watts" stroke="#2563eb" strokeWidth={2} />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </CardContent>
  </Card>
);

const Dashboard = ({ user, selectedRoom, setSelectedDevice }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [timeRange, setTimeRange] = useState("week"); // week, month, year

  const filteredDevices = selectedRoom
    ? user.devices.filter((device) => 
        device.room === selectedRoom.name &&
        device.name.toLowerCase().includes(searchTerm.toLowerCase())
      )
    : user.devices.filter((device) =>
        device.name.toLowerCase().includes(searchTerm.toLowerCase())
      );

  const getAnalytics = () => {
    if (selectedRoom) {
      return {
        totalWatts: 850,
        efficiency: 85,
        activeDevices: 4,
        trend: -5.2
      };
    }
    return {
      totalWatts: 2450,
      efficiency: 78,
      activeDevices: 12,
      trend: 3.8
    };
  };

  const analytics = getAnalytics();

  return (
    <div className="w-full max-w-7xl mx-auto space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between gap-4">
        <div className="flex items-center gap-4">
          {selectedRoom && (
            <button
              onClick={() => window.location.reload()}
              className="p-2 hover:bg-gray-100 rounded-full transition-colors"
            >
              <Home className="w-6 h-6" />
            </button>
          )}
          <h1 className="text-3xl font-bold text-gray-800">
            {selectedRoom ? selectedRoom.name : `Welcome, ${user.name}`}
          </h1>
        </div>
        
        <div className="relative w-1/3">
          <input
            type="text"
            placeholder="Search devices..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
        </div>
      </div>

      {/* Analytics Section */}
      <div className="grid grid-cols-4 gap-4">
        <AnalyticsCard
          title="Total Power Usage"
          value={analytics.totalWatts}
          unit="watts"
          trend={analytics.trend}
          icon={Zap}
        />
        <AnalyticsCard
          title="Efficiency Score"
          value={analytics.efficiency}
          unit="%"
          trend={-2.1}
          icon={Gauge}
        />
        <AnalyticsCard
          title="Active Devices"
          value={analytics.activeDevices}
          unit="devices"
          trend={1.5}
          icon={Battery}
        />
        <AnalyticsCard
          title="Usage Time"
          value={8.5}
          unit="hrs"
          trend={-1.2}
          icon={Timer}
        />
      </div>

      {/* Usage Chart */}
      <UsageChart 
        data={selectedRoom ? mockUsageData.rooms[selectedRoom.name] : mockUsageData.house}
        title={`Power Usage - ${selectedRoom ? selectedRoom.name : 'Whole House'}`}
      />

      {/* Devices Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredDevices.length > 0 ? (
          filteredDevices.map((device) => (
            <DeviceCard key={device.id} device={device} onClick={setSelectedDevice} />
          ))
        ) : (
          <div className="col-span-full text-center py-8">
            <p className="text-gray-500 text-lg">No devices found.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
