import React from "react";

function DevicePopup({ device, setSelectedDevice }) {
  if (!device) return null;

  return (
    <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-96 relative">
        <button
          className="absolute top-2 right-2 text-gray-600"
          onClick={() => setSelectedDevice(null)}
        >
          âœ–
        </button>
        <h2 className="text-xl font-bold mb-2">{device.name}</h2>
        <div className="flex">
          {/* Image on Left Side */}
          <img
            src={device.image}
            alt={device.name}
            className="w-24 h-24 object-contain mr-4"
          />
          <div>
            <p><strong>Device ID:</strong> {device.id}</p>
            <p><strong>Status:</strong> {device.status}</p>
            <p><strong>Room:</strong> {device.room}</p>
            <p><strong>Watt Usage:</strong> {device.watts} W</p>
            <p><strong>Cost per Hour:</strong> ${device.cost}/hr</p>
          </div>
        </div>
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded mt-4 w-full"
          onClick={() => setSelectedDevice(null)}
        >
          Close
        </button>
      </div>
    </div>
  );
}

export default DevicePopup;
