  }

  return (
    <div className="bg-blue-200 p-4 rounded-lg shadow">
      <h2 className="text-lg font-semibold mb-2">Weather</h2>
      <p className="text-xl font-bold">{weather.temperature}Â°C</p>
      <p className="text-md">{weather.condition}</p>
    </div>
  );
}

export default WeatherWidget;
