import React from "react";

function AccountSettings({ account }) {
  return (
    <div className="p-6 bg-white rounded shadow-md">
      <h2 className="text-2xl font-bold mb-4">Account Settings</h2>
      <label className="block mb-2">First Name:</label>
      <input type="text" value={account.firstName} className="p-2 border rounded w-full mb-2" readOnly />

      <label className="block mb-2">Last Name:</label>
      <input type="text" value={account.lastName} className="p-2 border rounded w-full mb-2" readOnly />

      <label className="block mb-2">Email(s):</label>
      {account.emails.map((email, index) => (
        <input key={index} type="email" value={email} className="p-2 border rounded w-full mb-2" readOnly />
      ))}

      <label className="block mb-2">Phone Number(s):</label>
      {account.phoneNumbers.map((phone, index) => (
        <input key={index} type="text" value={phone} className="p-2 border rounded w-full mb-2" readOnly />
      ))}

      <label className="block mb-2">Address:</label>
      <input type="text" value={account.address} className="p-2 border rounded w-full mb-2" readOnly />

      <h3 className="text-lg font-bold mt-4">Alerts</h3>
      {Object.keys(account.alerts).map((alertType, index) => (
        <div key={index}>
          <input type="checkbox" checked={account.alerts[alertType]} readOnly className="mr-2" />
          {alertType.replace(/([A-Z])/g, " $1").trim()}
        </div>
      ))}
    </div>
  );
}

export default AccountSettings;
