import React, { useState } from "react";

function DeviceCard({ name, status, image, id, cost, watts }) {
  const [showPopup, setShowPopup] = useState(false);

  return (
    <div>
      <div className="bg-white p-5 rounded-xl shadow-md flex flex-col items-center cursor-pointer" onClick={() => setShowPopup(true)}>
        <img src={image} alt={name} className="w-16 h-16 mb-3" />
        <h3 className="text-lg font-semibold">{name}</h3>
        <p className={`text-sm ${status === "On" ? "text-green-500" : "text-red-500"}`}>Status: {status}</p>
        <button className="bg-blue-500 text-white px-4 py-2 rounded mt-2 shadow-md">Toggle</button>
      </div>

      {showPopup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold mb-2">Edit Device</h2>
            <label className="block mb-2">Device Name:</label>
            <input type="text" defaultValue={name} className="p-2 border rounded w-full mb-2" />
            <p>Device ID: {id}</p>
            <p>Power Usage: {watts}W</p>
            <p>Cost per kWh: ${cost}</p>
            <img src={image} alt={name} className="w-16 h-16 mt-2" />
            <div className="flex justify-end mt-4">
              <button className="bg-gray-500 text-white px-4 py-2 rounded mr-2" onClick={() => setShowPopup(false)}>Close</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default DeviceCard;
