import React, { useState, useRef } from "react";
import { ChevronLeft, Save, X } from "lucide-react";
import lightbulb from "../assets/images/lightbulb.png";
import noniot from "../assets/images/noniot.png";
import television from "../assets/images/television.png";
import fridge from "../assets/images/fridge.png";

const deviceImages = {
  "Smart Light": lightbulb,
  "Television": television,
  "Fridge": fridge
};

function DevicePopup({ device = {}, setSelectedDevice, rooms = [] }) {
  const {
    name = "",
    id = "",
    watts = 0,
    cost = 0,
    room = "",
    // If your device object can contain notes from the server, uncomment:
    // notes: initialNotes = ""
  } = device;

  const [deviceName, setDeviceName] = useState(name);
  const [selectedRoom, setSelectedRoom] = useState(room);
  const [activeTab, setActiveTab] = useState("details");
  const [timeRange, setTimeRange] = useState("day");
  const [hasChanges, setHasChanges] = useState(false);
  const [showConfirmClose, setShowConfirmClose] = useState(false);

  // If you want to initialize with device.notes:
  // const [notes, setNotes] = useState(initialNotes);
  const [notes, setNotes] = useState("");

  // Ref for detecting swipe gestures
  const touchStartX = useRef(null);

  if (!device) {
    return null;
  }

  // Handle changes in any input field
  const handleChange = (field, value) => {
    if (field === "deviceName") setDeviceName(value);
    if (field === "selectedRoom") setSelectedRoom(value);
    if (field === "notes") setNotes(value);
    setHasChanges(true);
  };

  // Handle Save
  const handleSave = () => {
    console.log("Saving device changes:", {
      deviceName,
      selectedRoom,
      notes,
    });
    // Close the popup after saving
    setSelectedDevice(null);
  };

  // Handle Close with confirmation if there are unsaved changes
  const handleClose = () => {
    if (hasChanges) {
      setShowConfirmClose(true);
    } else {
      setSelectedDevice(null);
    }
  };

  // Handle touch start for swipe detection
  const handleTouchStart = (e) => {
    touchStartX.current = e.touches[0].clientX;
  };

  // Handle touch end for swipe detection
  const handleTouchEnd = (e) => {
    if (!touchStartX.current) return;

    const touchEndX = e.changedTouches[0].clientX;
    const diff = touchStartX.current - touchEndX;

    // Minimum swipe distance (in pixels)
    const minSwipeDistance = 50;

    if (Math.abs(diff) < minSwipeDistance) return;

    // Left swipe
    if (diff > 0 && activeTab === "details") {
      setActiveTab("usage");
    }
    // Right swipe
    else if (diff < 0 && activeTab === "usage") {
      setActiveTab("details");
    }

    touchStartX.current = null;
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
      <div 
        className="bg-white rounded-xl shadow-xl w-full max-w-2xl mx-4 overflow-hidden"
        onTouchStart={handleTouchStart}
        onTouchEnd={handleTouchEnd}
      >
        {/* Header with back button */}
        <div className="bg-gray-50 px-6 py-4 flex items-center justify-between border-b">
          <div className="flex items-center">
            <button
              onClick={handleClose}
              className="mr-3 p-2 rounded-full hover:bg-gray-200 transition-colors"
              aria-label="Close"
            >
              <ChevronLeft className="w-6 h-6" />
            </button>
            <h2 className="text-2xl font-bold">{name}</h2>
          </div>
          <div className="text-sm text-gray-500">ID: {id}</div>
        </div>

        {/* Device Image */}
        <div className="flex justify-center py-6 bg-gray-50">
          <div className="w-32 h-32 bg-white rounded-full shadow-md flex items-center justify-center">
            <img 
              src={deviceImages[name] || noniot}
              alt={name} 
              className="w-24 h-24 object-contain"
            />
          </div>
        </div>

        {/* Custom Tabs */}
        <div className="px-6">
          <div className="flex border-b mb-6">
            <button
              className={`px-6 py-4 text-lg ${
                activeTab === "details"
                  ? "border-b-2 border-blue-500 text-blue-500 font-medium"
                  : "text-gray-500"
              }`}
              onClick={() => setActiveTab("details")}
            >
              Device Details
            </button>
            <button
              className={`px-6 py-4 text-lg ${
                activeTab === "usage"
                  ? "border-b-2 border-blue-500 text-blue-500 font-medium"
                  : "text-gray-500"
              }`}
              onClick={() => setActiveTab("usage")}
            >
              Usage Analytics
            </button>
          </div>

          {/* Tab Content Container with same height */}
          <div className="min-h-[300px] pb-4">
            {/* Details Tab Content */}
            {activeTab === "details" && (
              <div className="space-y-6">
                <div>
                  <label className="block text-lg font-medium mb-2">Device Name</label>
                  <input
                    type="text"
                    className="p-3 border rounded-lg w-full text-lg"
                    value={deviceName}
                    onChange={(e) => handleChange("deviceName", e.target.value)}
                  />
                </div>

                <div className="grid grid-cols-2 gap-6">
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <label className="block text-sm font-medium text-gray-500 mb-1">Power Usage</label>
                    <p className="text-2xl font-semibold">{watts} W</p>
                  </div>
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <label className="block text-sm font-medium text-gray-500 mb-1">Cost per kWh</label>
                    <p className="text-2xl font-semibold">${cost.toFixed(2)}</p>
                  </div>
                </div>

                <div>
                  <label className="block text-lg font-medium mb-2">Room</label>
                  <select 
                    className="p-3 border rounded-lg w-full text-lg bg-white"
                    value={selectedRoom}
                    onChange={(e) => handleChange("selectedRoom", e.target.value)}
                  >
                    {rooms.map((room, index) => (
                      <option key={index} value={room.name || ""} className="p-2">
                        {room.name || "Unknown Room"}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            )}

            {/* Usage Tab Content */}
            {activeTab === "usage" && (
              <div>
                <div className="mb-6">
                  <div className="flex space-x-2 mb-4 overflow-x-auto pb-2 scrollbar-hide">
                    {["day", "week", "month", "year"].map((range) => (
                      <button
                        key={range}
                        onClick={() => setTimeRange(range)}
                        className={`px-4 py-3 rounded-lg text-lg flex-shrink-0 ${
                          timeRange === range
                            ? "bg-blue-500 text-white"
                            : "bg-gray-100"
                        }`}
                      >
                        {range.charAt(0).toUpperCase() + range.slice(1)}
                      </button>
                    ))}
                  </div>
                  <div className="h-64 bg-gray-50 rounded-lg flex items-center justify-center">
                    <p className="text-gray-500 text-lg">Usage chart will be displayed here</p>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Notes Section */}
          <div className="mt-4 mb-6">
            <label className="block text-lg font-medium mb-2">Notes</label>
            <textarea
              className="p-3 border rounded-lg w-full text-lg"
              rows={4}
              value={notes}
              onChange={(e) => handleChange("notes", e.target.value)}
              placeholder="Add notes about this device..."
            />
          </div>
        </div>

        {/* Action Buttons - Fixed at bottom */}
        <div className="border-t p-4 flex justify-between bg-gray-50">
          <button
            className="flex items-center justify-center gap-2 bg-gray-200 text-gray-800 px-6 py-3 rounded-lg hover:bg-gray-300 transition-colors text-lg"
            onClick={handleClose}
          >
            <X className="w-5 h-5" />
            Cancel
          </button>
          <button
            className="flex items-center justify-center gap-2 bg-blue-500 text-white px-8 py-3 rounded-lg hover:bg-blue-600 transition-colors text-lg"
            onClick={handleSave}
          >
            <Save className="w-5 h-5" />
            Save Changes
          </button>
        </div>

        {/* Confirm Close Modal */}
        {showConfirmClose && (
          <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
            <div className="bg-white rounded-xl p-6 w-full max-w-md mx-4">
              <h3 className="text-xl font-bold mb-4">Unsaved Changes</h3>
              <p className="mb-6">You have unsaved changes. Are you sure you want to close without saving?</p>
              <div className="flex justify-end gap-3">
                <button
                  className="px-5 py-3 bg-gray-200 rounded-lg"
                  onClick={() => setShowConfirmClose(false)}
                >
                  Continue Editing
                </button>
                <button
                  className="px-5 py-3 bg-red-500 text-white rounded-lg"
                  onClick={() => setSelectedDevice(null)}
                >
                  Discard Changes
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default DevicePopup;
