import React, { useState } from "react";

function AccountSettings({ setShowAccountSettings, userData }) {
  const [selectedTimes, setSelectedTimes] = useState({});
  const [isSelecting, setIsSelecting] = useState(false);
  const [selectionStart, setSelectionStart] = useState(null);
  const [currentSelection, setCurrentSelection] = useState(null);
  const [selectionValue, setSelectionValue] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [alwaysOnDevices, setAlwaysOnDevices] = useState([]);

  const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
  const hours = Array.from({ length: 24 }, (_, i) => `${i}:00`);

  // Filter devices based on search term (by name or ID)
  const filteredDevices = userData.devices.filter(
    (device) =>
      (device.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        device.id.toString().includes(searchTerm)) &&
      !alwaysOnDevices.some((d) => d.id === device.id)
  );

  // Add device to Always-On list
  const addDevice = (device) => {
    setAlwaysOnDevices((prev) => [...prev, device]);
    setSearchTerm("");
  };

  // Remove device from Always-On list
  const removeDevice = (deviceId) => {
    setAlwaysOnDevices((prev) => prev.filter((d) => d.id !== deviceId));
  };

  // Helper function to get cell coordinates
  const getCellCoordinates = (day, hour) => ({
    dayIndex: daysOfWeek.indexOf(day),
    hourIndex: hours.indexOf(hour),
  });

  // Helper function to check if a cell is within selection range
  const isCellInRange = (day, hour, start, end) => {
    if (!start || !end) return false;
    const cell = getCellCoordinates(day, hour);
    const startCoords = getCellCoordinates(start.day, start.hour);
    const endCoords = getCellCoordinates(end.day, end.hour);

    return (
      cell.dayIndex >= Math.min(startCoords.dayIndex, endCoords.dayIndex) &&
      cell.dayIndex <= Math.max(startCoords.dayIndex, endCoords.dayIndex) &&
      cell.hourIndex >= Math.min(startCoords.hourIndex, endCoords.hourIndex) &&
      cell.hourIndex <= Math.max(startCoords.hourIndex, endCoords.hourIndex)
    );
  };

  // Handle mouse down to start selection
  const handleMouseDown = (day, hour) => {
    setIsSelecting(true);
    setSelectionStart({ day, hour });
    setCurrentSelection({ day, hour });
    const currentValue = selectedTimes[day]?.[hour] || false;
    setSelectionValue(!currentValue);
    setSelectedTimes((prev) => {
      const updated = { ...prev };
      if (!updated[day]) updated[day] = {};
      updated[day][hour] = !currentValue;
      return updated;
    });
  };

  // Handle mouse enter while selecting
  const handleMouseEnter = (day, hour) => {
    if (isSelecting) {
      setCurrentSelection({ day, hour });
    }
  };

  // Handle mouse up to end selection
  const handleMouseUp = () => {
    if (isSelecting && selectionStart && currentSelection) {
      const updatedTimes = { ...selectedTimes };
      daysOfWeek.forEach((day) => {
        hours.forEach((hour) => {
          if (isCellInRange(day, hour, selectionStart, currentSelection)) {
            if (!updatedTimes[day]) updatedTimes[day] = {};
            updatedTimes[day][hour] = selectionValue;
          }
        });
      });
      setSelectedTimes(updatedTimes);
    }
    setIsSelecting(false);
    setSelectionStart(null);
    setCurrentSelection(null);
  };

  return (
    <div className="p-6 bg-white rounded shadow-md overflow-y-auto h-screen">
      {/* Back Button */}
      <button
        className="bg-gray-500 text-white p-2 rounded mb-4"
        onClick={() => setShowAccountSettings(false)}
      >
        Back
      </button>

      {/* Header */}
      <h2 className="text-2xl font-bold mb-4">Account Settings</h2>

      {/* Personal Information */}
      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block mb-2">First Name:</label>
          <input type="text" className="p-2 border rounded w-full mb-2" defaultValue={userData.account.firstName} />

          <label className="block mb-2">Last Name:</label>
          <input type="text" className="p-2 border rounded w-full mb-2" defaultValue={userData.account.lastName} />

          <label className="block mb-2">Email:</label>
          <input type="email" className="p-2 border rounded w-full mb-2" defaultValue={userData.account.emails[0]} />

          <label className="block mb-2">Phone Number:</label>
          <input type="text" className="p-2 border rounded w-full mb-2" defaultValue={userData.account.phoneNumbers[0]} />
        </div>

        {/* Address Section */}
        <div>
          <label className="block mb-2">Address Line 1:</label>
          <input type="text" className="p-2 border rounded w-full mb-2" defaultValue={userData.account.address.line1} />

          <label className="block mb-2">Address Line 2:</label>
          <input type="text" className="p-2 border rounded w-full mb-2" defaultValue={userData.account.address.line2} />

          <div className="grid grid-cols-3 gap-2">
            <div>
              <label className="block mb-2">City:</label>
              <input type="text" className="p-2 border rounded w-full mb-2" defaultValue={userData.account.address.city} />
            </div>
            <div>
              <label className="block mb-2">State:</label>
              <input type="text" className="p-2 border rounded w-full mb-2" defaultValue={userData.account.address.state} />
            </div>
            <div>
              <label className="block mb-2">Zip Code:</label>
              <input type="text" className="p-2 border rounded w-full mb-2" defaultValue={userData.account.address.zipCode} />
            </div>
          </div>
        </div>
      </div>

      {/* Always-On Devices Search (by Name or ID) */}
      <h3 className="text-xl font-semibold mt-6 mb-2">Always-On Devices:</h3>
      <input
        type="text"
        placeholder="Search by name or ID..."
        className="p-2 border rounded w-full mb-2"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <div className="bg-white rounded-lg p-2 max-h-64 overflow-y-auto">
        {filteredDevices.map((device) => (
          <div key={device.id} className="flex justify-between items-center p-2 hover:bg-gray-100 rounded">
            <div>
              <p className="font-semibold">{device.name}</p>
              <p className="text-sm text-gray-600">ID: {device.id} - {device.room}</p>
            </div>
            <button onClick={() => addDevice(device)} className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600">
              Add
            </button>
          </div>
        ))}
      </div>

      {/* Save Button */}
      <button className="bg-green-500 text-white p-2 rounded mt-4 w-full">Save Changes</button>
    </div>
  );
}

export default AccountSettings;
